@include theme {
  .pl-item {
    transition: all 300ms $transition;
    @include public-style;
  }

  .pl-list {
    display: block;
    width: 100%;
    position: relative;
    @include public-style;

    .pl-item-block {
      display: block;
      width: 100% !important;
    }
  }

  $directions: (
          top:(x:0, y:-30%),
          left:(x:-80%, y:0),
          right:(x:80%, y:0),
          bottom:(x:0, y:30%),
          left-top:(x:-80%, y:-30%),
          top-left:(x:-80%, y:-30%),
          right-top:(x:80%, y:-30%),
          top-right:(x:80%, y:-30%),
          left-bottom:(x:-80%, y:30%),
          bottom-left:(x:-80%, y:30%),
          right-bottom:(x:80%, y:30%),
          bottom-right:(x:80%, y:30%),
  );

  @each $key, $value in $directions {
    .pl-list-move-#{$key}-enter-from {
      opacity: 0;
      transform: translateX(map_get($value, x)) translateY(map_get($value, y));
    }

    .pl-list-move-#{$key}-leave-to {
      opacity: 0 !important;
    }

    .pl-list-move-#{$key}-leave-active {
      position: absolute;
    }
  }
}

$screen-sm: 768px;
$screen-md: 992px;
$screen-lg: 1200px;
$screen-xl: 1920px;
$cell-num: 24;
$flexs: (
        start:(justify-content:flex-start),
        center:(justify-content:center),
        end:(justify-content:flex-end),
        space-between:(justify-content:space-between),
        space-around:(justify-content:space-around),
        top:(align-items:flex-start),
        middle:(align-items:center),
        bottom:(align-items:flex-end),
);
$screens: (
        xs:(max-width:$screen-sm),
        sm:(min-width:$screen-sm),
        md:(min-width:$screen-md),
        lg:(min-width:$screen-lg),
        xl:(min-width:$screen-xl),
);

@mixin res($key, $map: $screens) {
  // 循环断点Map，如果存在则返回
  @if map-has-key($map, $key) {
    @media only screen and #{inspect(map-get($map, $key))} {
      @content;
    }
  } @else {
    @warn "Undefeined points: `#{$map}`";
  }
}

@include theme {
  .pl-row {
    height: auto;
    position: relative;
    display: block;

    &:not(:last-child) {
      margin-bottom: 16px;
    }

    &, & * {
      box-sizing: border-box;
    }

    /*flex布局*/
    &.pl-row {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;

      &:before,
      &:after {
        display: flex;
      }

      /*flex布局*/
      @each $flexType, $flexValue in $flexs {
        &.pl-row-flex-#{$flexType} {
          @each $key, $value in $flexValue {
            #{$key}: #{$value}
          }
        }
      }
    }

    /*响应式*/
    .pl-col {
      display: block;
      float: left;

      @for $i from 1 through $cell-num {
        &.pl-col-span-#{$i} {
          width: (1 / $cell-num * $i * 100) * 1%;
        }
        &.pl-col-offset-#{$i} {
          margin-left: (1 / $cell-num * $i * 100) * 1%;
        }
        &.pl-col-pull-#{$i} {
          position: relative;
          right: (1 / $cell-num * $i * 100) * 1%;
        }
        &.pl-col-push-#{$i} {
          position: relative;
          left: (1 / $cell-num * $i * 100) * 1%;
        }
        &.pl-col-order-#{$i} {
          order: $i;
        }
      }

      @each $size, $px in $screens {
        @include res(inspect($size)) {
          @for $i from 1 through $cell-num {
            &.pl-col-#{$size}-#{$i} {
              width: (1 / $cell-num * $i * 100) * 1%;
            }
          }
        }
      }
    }
  }

  @each $size, $px in $screens {
    @include res(inspect($size)) {
      .hidden-#{$size}-only {
        display: none;
      }
    }
  }
}

