  $dialogTransition: (
          left:translate3d(-100%, 0, 0),
          right:translate3d(100%, 0, 0),
          top:translate3d(0, -100%, 0),
          bottom:translate3d(0, 100%, 0),
  );

  @each $key, $value in $dialogTransition {
    .pl-transition-dialog-#{$key} {
      .pl-dialog-body {
