@include theme {

  .pl-number {
    display: inline-block;
    width: initial;

    .pl-input-inner {
      position: relative;
      padding-top: 1px;
      padding-bottom: 1px;
      display: block;

      input {
        width: 100%;
        height: 100%;
        border: none;
        outline: none;
        float: left;
        color: $itc;
        font-size: inherit;
        box-sizing: border-box;

        &::placeholder {
          color: $ipc;
        }

        &::-ms-clear {
          display: none;
        }

        &::-webkit-outer-spin-button,
        &::-webkit-inner-spin-button {
          -webkit-appearance: none !important;
          margin: 0;
        }

        &:-webkit-autofill {
          background-color: white !important;
        }
      }

      .pl-number-prepend-button, .pl-number-append-button {
        position: absolute;
        top: 1px;
        bottom: 1px;
        width: 30px;
        text-align: center;
        background-color: #f5f7fa;
        cursor: pointer;
        outline: none;
        user-select: none;
        transition: background-color linear 150ms;

        .pl-icon {
          vertical-align: 0;
        }

        &.pl-number-prepend-button {
          left: 1px;
          border-right: solid 1px #dcdfe6;
        }

        &.pl-number-append-button {
          right: 1px;
          border-left: solid 1px #dcdfe6;
        }

        &:active {
          background-color: #e1e1e1;
        }
      }
    }

    @include shapeMixin(input) {
      .pl-number-prepend-button {
        border-top-left-radius: $value;
        border-bottom-left-radius: $value;
      }
      .pl-number-append-button {
        border-top-right-radius: $value;
        border-bottom-right-radius: $value;
      }
    }
    @include statusMixin(input) {
      .pl-number-prepend-button, .pl-number-append-button {
        &:after {
          border-color: $value;
        }
      }
    }

    &:not(.pl-number-hide-button) {
      .pl-input-inner {
        padding-left: 30px !important;
        padding-right: 30px !important;

        input {
          text-align: center !important;
          padding: 0 12px;
        }
      }
    }

    &.pl-number-disabled {
      input {
        background-color: $disabled;
        cursor: not-allowed;
        color: #c0c4cc;
      }

      .pl-number-prepend-button, .pl-number-append-button {
        background-color: $disabled;
        cursor: not-allowed;
        color: $disabledText;
      }
    }
  }
}

@include theme {
  .pl-toggle {
    background-color: $disabled;
    position: relative;
    outline: none;
    display: inline-block;
    vertical-align: middle;
    user-select: none;
    transition: background-color 300ms;
    cursor: pointer;

    .pl-toggle-inner {
      content: '';
      position: absolute;
      left: 2px;
      top: 2px;
      bottom: 2px;
      background: white;
      box-shadow: 0 3px 12px rgba(0, 0, 0, .16), 0 3px 1px rgba(0, 0, 0, .1);
      transition: all 0.3s;
      border-radius: plv(sizeLarge);
    }

    &:before {
      position: absolute;
      left: 2px;
      right: 2px;
      top: 2px;
      bottom: 2px;
      border-radius: plv(sizeLarge);
      background-color: #fff;
      content: "";
      transform: scaleX(1);
      /*transform: scale3d(0, 0, 0);*/
      transition: transform .3s;
    }

    @include sizeMixin(toggle) {
      $value: $value - 6px;
      height: $value;
      width: $value*52/32;
      border-radius: plv(sizeLarge);

      &.pl-toggle-on {
        .pl-toggle-inner {
          left: $value/2+6;
        }
      }
      .pl-toggle-inner {
        right: $value/2+6;
      }
      &.pl-toggle-active {
        .pl-toggle-inner {
          right: $value/2;
        }

        &.pl-toggle-on .pl-toggle-inner {
          left: $value/2;
        }
      }
    }

    &.pl-toggle-on {
      &:before {
        transform: scale3d(0, 0, 0);
      }

      .pl-toggle-inner {
        right: 2px;
      }
    }

    &.pl-toggle-active {
      .pl-toggle-inner {
        left: 2px;
      }

      &.pl-toggle-on .pl-toggle-inner {
        right: 2px;
      }
    }

    @include statusMixin(toggle) {
      &.pl-toggle-on {
        background-color: $value;
      }
      &:after {
        border-color: $value;
      }
      &:focus {
        .pl-toggle-inner {
          background-color: mix(white, $value, 90%);
        }
      }
    }

    &.pl-toggle-disabled {
      cursor: not-allowed;

      background-color: $disabled;

      &:before {
        background-color: $disabled;
      }

      .pl-toggle-inner {
        background-color: $disabled !important;
      }

      &.pl-toggle-on {
        @include statusMixin(toggle) {
          background-color: rgba($value, 0.5);
        }
      }
    }
  }
}

@include theme {
  .pl-slider {
    @include public-style;
    cursor: pointer;
    display: inline-block;
    position: relative;
    background-color: rgba(black, 0.15);
    border-radius: 100px;

    .pl-slider-progress {
      border-radius: 100px;
      position: absolute;
      top: 0;
      bottom: 0;
      left: 0;
      right: 0;
      display: flex;
      align-items: center;
      justify-content: space-between;

      .pl-slider-dot-wrapper {
        width: 0;
        height: 0;
        overflow: visible;
        display: flex;
        align-items: center;
        justify-content: center;
        user-select: none;

        .pl-slider-dot {
          height: 16px;
          width: 16px;
          background-color: white;
          box-shadow: 0 0 6px #aaa;
          border-radius: 100px;
          display: flex;
          align-items: center;
          justify-content: center;
          flex-shrink: 0;
          transition: all 0.15s cubic-bezier(0, 1.55, 1, 1.52);
          transform-origin: center center;

          .pl-slider-dot-inner {
            width: 6px;
            height: 6px;
            border-radius: 100px;
            position: relative;

            .pl-slider-dot-tooltip {
              transition: all 300ms $transition;
              opacity: 0;
              background-color: rgba(black, 0.8);
              color: white;
              padding: 3px 6px;
              position: absolute;
              left: 50%;
              transform: translateX(-50%);
              bottom: calc(100% + 9px);
              border-radius: 4px;
              font-size: 12px;
              pointer-events: none;
              &:hover {
                opacity: 0.5;
              }
            }
          }

          &:hover {
            .pl-slider-dot-tooltip {
              opacity: 1;
            }
          }
        }
      }
    }

    .pl-slider-step {
      position: absolute;
      top: 0;
      bottom: 0;
      left: 0;
      right: 0;
      display: flex;
      align-items: center;
      justify-content: space-evenly;

      .pl-slider-step-item-wrapper {
        display: inline-flex;
        align-items: center;
        justify-content: center;
        width: 0;
        height: 0;
        overflow: visible;

        .pl-slider-step-item {
          background-color: rgba(black, 0.3);
          border-radius: 100px;
          flex-shrink: 0;
        }
      }
    }

    &.pl-slider-touching {
      .pl-slider-dot {
        transform: scale(1.4);

        .pl-slider-dot-tooltip {
          opacity: 1 !important;
        }
      }
    }

    .pl-slider-dot:hover {
      transform: scale(1.4);
      transform-origin: center center;
      transition: all 0.15s cubic-bezier(0, 1.55, 1, 1.52);
    }

    @include statusMixin(slider) {
      .pl-slider-progress {
        &, & .pl-slider-dot-inner {
          background-color: $value;
        }
      }
    }

    &.pl-slider-vertical {
      .pl-slider-progress, .pl-slider-step {
        display: flex;
        flex-direction: column;
      }
    }

    &.pl-slider-horizontal {
      .pl-slider-progress, .pl-slider-step {
        display: flex;
        flex-direction: row;
      }
    }

    &.pl-slider-align-start {
      .pl-slider-dot-wrapper-start {
        opacity: 0;
        z-index: 0;
      }

      .pl-slider-dot-wrapper-end {
        z-index: 1;
      }
    }

    &.pl-slider-align-end {
      .pl-slider-dot-wrapper-end {
        opacity: 0;
        z-index: 0;
      }

      .pl-slider-dot-wrapper-start {
        z-index: 1;
      }
    }

    &.pl-slider-range {
      .pl-slider-dot-wrapper {
        opacity: 1;
      }
    }

    &.pl-slider-place-end {
      .pl-slider-dot-wrapper-start {
        z-index: 2;
      }
    }
  }
}

