    height: 40px;

    .pl-arrow-step {
      flex: 1;
      height: 100%;
      box-sizing: border-box;
      display: flex;
      align-items: center;
      position: relative;

      .pl-arrow-step-sequence {
        font-style: italic;
        font-size: 1.2em;
        opacity: 0.6;
      }

      .pl-arrow-step-content {
        flex: 1;
        overflow: hidden;
        height: 100%;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        padding: 0 16px;
        color: white;
      }

      .pl-triangle {

        &.pl-triangle-direction-top, &.pl-triangle-direction-bottom {
          position: absolute;
          right: 100%;

          &.pl-triangle-direction-top {
            bottom: 0;
          }

          &.pl-triangle-direction-bottom {
            top: 0;
          }
        }
      }

      .pl-arrow-step-content, .pl-triangle {
        @include transition;
      }

      &:not(:first-child) {
        margin-left: 4px;
      }

      &.pl-arrow-step-status-wait {
        .pl-arrow-step-content {
          background-color: $disabled;
          color: $itl;
        }

        .pl-triangle {
          color: $disabled;
        }
      }

      &.pl-arrow-step-status-process, &.pl-arrow-step-status-finish {
        .pl-arrow-step-content {
          background-color: $colorPrimary;
          color: white;
        }

        .pl-triangle {
          color: $colorPrimary;
        }
      }

      &.pl-arrow-step-status-success {
        .pl-arrow-step-content {
          background-color: $colorSuccess;
          color: white;
        }

        .pl-triangle {
          color: $colorSuccess;
        }
      }

      &.pl-arrow-step-status-error {
        .pl-arrow-step-content {
          background-color: $colorError;
          color: white;
        }

        .pl-triangle {
          color: $colorError;
        }
      }
    }
  }
}

@include theme{
  .pl-step-group {

    .pl-step {
      .pl-step-title {
        font-size: 16px;
        color: $ihc;
      }

      .pl-step-content {
        font-size: 14px;
      }

      .pl-step-dot {
        width: 12px;
        height: 12px;
        border-radius: 12px;
        display: inline-block;
      }
    }

    &:not(.pl-step-group-dot-icon ) {
      .pl-step-icon {
        width: 32px;
        height: 32px;
        font-size: 16px;
        line-height: 32px;
        text-align: center;
        border-radius: 32px;
        border: solid 1px $ibc;
        display: inline-block;
        color: $icc;
        margin: 0 8px;

        .pl-icon, .pl-loading {
          font-size: 1.8em;
        }

        .pl-step-number {
          .pl-icon {
            font-size: 1.2em;
          }
        }
      }
    }

    &.pl-step-group-horizontal {
      display: flex;
      align-items: center;

      .pl-step {
        flex: 1;

        .pl-step-divider {
          flex: 1;
          height: 1px;
          background-color: $ibl;
        }

        .pl-step-head, .pl-step-body {
          display: flex;
        }

        .pl-step-head {
          align-items: center;
          margin-bottom: 8px;

          .pl-step-title {
            margin-right: 8px;
          }

          .pl-step-dot {
            margin-right: 8px;
          }
        }

        .pl-step-body {
          align-items: flex-start;

          .pl-step-icon {
            border: none;
          }

          .pl-step-title {
            margin-bottom: 8px;
          }
        }


        &.pl-step-last {
          flex: initial;
        }
      }

      &.pl-step-group-title-align-bottom {
        .pl-step {
          &:first-child {
            .pl-step-head {
              .pl-step-divider:first-child {
                background-color: transparent !important;
              }
            }
          }

          &:last-child {
            flex: 1;

            .pl-step-head {
              .pl-step-divider:last-child {
                background-color: transparent !important;
              }
            }
          }
        }

        .pl-step-body {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
        }
      }
    }

    &.pl-step-group-vertical {
      display: flex;
      flex-direction: column;
      flex-wrap: revert;

      .pl-step {
        display: flex;

        .pl-step-head {
          display: flex;
          flex-direction: column;
          align-items: center;

          .pl-step-divider {
            flex: 1;
            width: 1px;
            background-color: $ibl;
          }
        }

        .pl-step-body {
          flex: 1;
          display: flex;
          flex-direction: column;

          .pl-step-title {
            line-height: 32px;
          }

          .pl-step-content {
            padding: 8px 0;
          }
        }

        &:last-child {
          .pl-step-head .pl-step-divider {
            display: none;
          }
        }
      }
    }

    .pl-step {
      &.pl-step-has-icon {
        .pl-step-head {
          .pl-step-icon {
            border: none;
            position: relative;
            top: 1px;
          }
        }
      }

      &.pl-step-status-wait {
        .pl-step-title {
          color: $icc;
        }

        .pl-step-content {
          color: $icc;
        }

        .pl-step-dot {
          border: solid 1px $icc;
          background-color: white;
        }
      }

      &.pl-step-status-finish {
        .pl-step-head {
          .pl-step-icon {
            border-color: $colorPrimary;
            color: $colorPrimary;
          }

          .pl-step-dot {
            background-color: $colorPrimary;
          }

          .pl-step-divider.pl-step-divider-next {
            background-color: $colorPrimary;
          }
        }

        & + .pl-step {
          .pl-step-head {
            .pl-step-divider-prev {
              background-color: $colorPrimary;
            }
          }
        }
      }

      &.pl-step-status-process {

        .pl-step-head {
          .pl-step-icon {
            border-color: $colorPrimary;
            background-color: $colorPrimary;
            color: white;
          }

          .pl-step-title {
            color: $colorPrimary;
          }

          .pl-step-dot {
            border: solid 4px $colorPrimary;
            box-sizing: border-box;
            background-color: white;
          }
        }

        .pl-step-body {
          .pl-step-content {
            color: $colorPrimary;
          }

          .pl-step-title {
            color: $colorPrimary;
          }
        }

        &.pl-step-has-icon {
          .pl-step-icon {
            border-color: transparent;
            background-color: transparent;
            color: $colorPrimary;
          }
        }
      }

      &.pl-step-status-error {

        .pl-step-head {
          .pl-step-icon {
            border-color: $colorError;
            background-color: $colorError;
            color: white;
          }

          .pl-step-title {
            color: $colorError;
          }

          .pl-step-dot {
            background-color: $colorError;
          }
        }

        .pl-step-body {
          .pl-step-content {
            color: $colorError;
          }

          .pl-step-title {
            color: $colorError;
          }
        }

        &.pl-step-has-icon {
          .pl-step-icon {
            border-color: transparent;
            background-color: transparent;
            color: $colorError;
          }
        }
      }
    }

    &.pl-step-group-mini {

      &:not(.pl-step-group-dot-icon) {
        .pl-step-icon {
          width: 20px;
          height: 20px;
          font-size: 12px;
          line-height: 20px;
          text-align: center;
          border-radius: 20px;
          border: solid 1px $ibc;
          display: inline-block;
          color: $icc;
          margin: 0 8px;

          .pl-icon, .pl-loading {
            font-size: 1.8em;
          }

          .pl-step-number {
            .pl-icon {
              font-size: 1.2em;
            }
          }
        }
      }

      .pl-step-title {
        font-size: 14px;
      }

      .pl-step-content {
        font-size: 12px;
      }

      &.pl-step-group-vertical {
        .pl-step {
          .pl-step-body {
            .pl-step-title {
              line-height: 20px;
            }
          }
        }
      }
    }

    &.pl-step-group-dot-icon {
      .pl-step {
        .pl-step-head {
          .pl-step-icon {
            background-color: transparent;
          }
        }
      }

      &.pl-step-group-horizontal:not(.pl-step-group-vertical) {
        .pl-step {
          .pl-step-content {
            padding-left: 20px;
          }
        }
      }

      &.pl-step-group-title-align-bottom {
        .pl-step .pl-step-head .pl-step-dot {
          margin-right: 0;
        }
      }

      &.pl-step-group-vertical {
        .pl-step-head {
          margin-right: 8px;
          position: relative;

          .pl-step-divider {
            position: absolute;
            left: 5px;
            top: 22px;
            bottom: -10px;
          }
        }

        .pl-step-icon {
          margin-top: 10px;
          height: 12px;
          display: inline-block;

          .pl-step-dot {
            float: left;
          }
        }

        &.pl-step-group-mini {
          .pl-step-icon {
            margin-top: 5px;
          }

          .pl-step-head {
            .pl-step-divider {
              position: absolute;
              left: 5px;
              top: 16px;
              bottom: -5px;
            }
          }
        }
      }
    }
  }
}

@include theme {

  /*---------------------------------------popper start-------------------------------------------*/

  .pl-popper {
    position: fixed;

    .plain-popper-content {
      border-radius: 2px;
      pointer-events: none !important;
      background-color: white;

      //background-color: black;
      //color: white;
      //height: 120px;

      .pl-popper-title {
        min-height: 32px;
        margin: 0;
        padding: 5px 16px 4px;
        color: rgba(0, 0, 0, .85);
        font-weight: 500;
        border-bottom: 1px solid #f0f0f0;
        font-size: 14px;
        box-sizing: border-box;
      }

      .pl-popper-content-inner {
        color: rgba(0, 0, 0, .85);
        font-size: 12px;
        padding: 12px 16px;
        box-sizing: border-box;

        p {
          margin: 0;
        }
      }
    }

    &.pl-popper-no-content-padding {
      .pl-popper-content-inner {
        padding-left: 0;
        padding-right: 0;
      }
    }

    /*box-shadow*/
    &.plain-popper {
      &[direction=top] {
        .plain-popper-content {
          box-shadow: 0 -1px 10px 2px rgba(0, 0, 0, 0.15);
        }
      }

      &[direction=bottom] {
        .plain-popper-content {
          box-shadow: 0 1px 10px 2px rgba(0, 0, 0, 0.15);
        }
      }

      &[direction=left] {
        .plain-popper-content {
          box-shadow: -1px 0 10px 2px rgba(0, 0, 0, 0.15);
        }
      }

      &[direction=right] {
        .plain-popper-content {
          box-shadow: 1px 0 10px 2px rgba(0, 0, 0, 0.15);
        }
      }
    }

    &.pl-popper-show {
      .plain-popper-content {
        pointer-events: auto !important;
      }
    }

    &:not(.pl-popper-show) {
      .plain-popper-content {
        box-shadow: none;
      }

      &:not(.pl-popper-open) {
        .plain-popper-content {
          transition: none !important;
        }
      }
    }

    /*&:not(.pl-popper-ready) {
      opacity: 0;
      pointer-events: none;
    }*/

    &:not(.pl-popper-show-arrow) {
      .plain-popper-arrow {
        display: none;
      }
    }

    /*---------------------------------------transition-------------------------------------------*/

    /*fade*/

    &.pl-transition-fade {
      .plain-popper-content {
        transition: opacity 150ms linear;
        opacity: 0;
      }

      &.pl-popper-show {
        .plain-popper-content {
          opacity: 1;
        }
      }
    }

    /*scale*/

    &.pl-transition-scale {
      .plain-popper-content {
        transition: all 200ms $transitionFlexible;
        transform: scale(0.5);
        opacity: 0;
      }

      &.pl-popper-show {
        .plain-popper-content {
          transform: scale(1);
          opacity: 1;
        }
      }
    }

    /*scale-y*/

    &.pl-transition-scale-y {
      .plain-popper-content {
        transition: all 300ms $transition;

        transform: scaleY(0);
      }

      &.pl-popper-show {
        .plain-popper-content {
          transform: scaleY(1);
        }
      }
    }

    /*drop*/

    &.pl-transition-popper-drop {

      .plain-popper-content {
        transition: all 200ms $transition2;
        opacity: 0;
      }

      $duration: 60%;

      &.plain-popper[direction=top] {
        .plain-popper-content {
          transform: translate3d(0, $duration, 0);
        }
      }

      &.plain-popper[direction=bottom] {
        .plain-popper-content {
          transform: translate3d(0, -$duration, 0);
        }
      }

      &.plain-popper[direction=left] {
        .plain-popper-content {
          transform: translate3d($duration, 0, 0);
        }
      }

      &.plain-popper[direction=right] {
        .plain-popper-content {
          transform: translate3d(-$duration, 0, 0);
        }
      }

      &.pl-popper-show {
        &.plain-popper .plain-popper-content {
          transform: translate3d(0, 0, 0);
          opacity: 1;
        }
      }

    }

  }
}

@include theme {
