@include theme {
  .pl-carousel {
    position: relative;

    &:not(.pl-carousel-vertical) {
      overflow-x: hidden;
    }

    .pl-carousel-item, .pl-carousel-cover, .pl-carousel-operator, .pl-carousel-indicator {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      pointer-events: none;

      &.pl-carousel-cover, &.pl-carousel-operator, &.pl-carousel-indicator {
        z-index: 99;
      }
    }

    .pl-carousel-item {
      &.pl-carousel-item-animating {
        transition: transform $transition 800ms;
      }

      pointer-events: auto;
    }

    .pl-carousel-cover {
      pointer-events: auto;
    }

    .pl-carousel-operator {
      display: flex;
      align-items: center;
      justify-content: space-between;
      padding: 0 20px;

      .pl-carousel-operator-btn {
        transition: background-color 300ms linear;
        cursor: pointer;
        pointer-events: auto;
        background-color: rgba(white, 0.3);
        width: 36px;
        height: 36px;
        border-radius: 36px;
        display: flex;
        align-items: center;
        justify-content: center;
        color: white;
        user-select: none;

        &:hover:not(:active) {
          background-color: rgba(white, 0.6);
        }
      }
    }

    .pl-carousel-indicator {
      display: flex;
      align-items: flex-end;
      justify-content: center;
      padding: 20px;
      flex-wrap: nowrap;

      .pl-carousel-indicator-item {
        pointer-events: auto;
        width: 20px;
        height: 3px;
        background-color: white;
        opacity: 0.3;
        transition: all 200ms linear;
        cursor: pointer;

        &:not(:last-child) {
          margin-right: 8px;
        }

        &.pl-carousel-indicator-item-active {
          opacity: 1;
          width: 30px;
        }

        &:hover {
          opacity: 0.8;
        }
      }
    }

    &.pl-carousel-vertical {
      overflow-y: hidden;

      .pl-carousel-operator {
        flex-direction: column;
        padding: 20px;
        align-items: flex-end;

        .pl-carousel-operator-btn:nth-child(1) {
          transform: rotate(90deg);
        }

        .pl-carousel-operator-btn:nth-child(2) {
          transform: rotate(90deg);
        }
      }

      .pl-carousel-indicator {
        flex-direction: column;
        padding: 20px 36px;

        .pl-carousel-indicator-item {
          height: 8px;
          width: 3px;
          margin-right: 0;

          &.pl-carousel-indicator-item-active {
            height: 16px;
          }

          &:not(:last-child) {
            margin-bottom: 4px;
          }
        }
      }
    }

    &.pl-carousel-card {
      .pl-carousel-item {
        right: initial;
      }

      .pl-carousel-indicator {
        .pl-carousel-indicator-item {
          background-color: rgba(black, 0.5);
        }
      }
    }
  }
}

@include theme {
  .pl-color-panel {
    display: inline-block;
    font-size: 0;
    padding: 0 12px;

    & > div:first-child {
      white-space: nowrap;
    }

    .pl-color-hue-slider {
      margin-top: 8px;
      display: block;
    }

    .pl-color-alpha-slider {
      margin-left: 8px;
    }

    .pl-color-panel-input-group {
      margin-top: 8px;

      .pl-input {

        .pl-input-inner {
          text-align: center;
          border-top-right-radius: 0;
          border-bottom-right-radius: 0;
          border-right: none;
        }
      }

      .pl-button-group {
        & > .pl-button:first-child {
          border-top-left-radius: 0;
          border-bottom-left-radius: 0;
          border-left: none;
        }
      }
    }
  }
}

@include theme {

  .pl-color-button {
    border-radius: 2px;
    height: 24px;
    width: 24px;
    border: solid 1px $ibc;
    padding: 2px;
    box-sizing: border-box;
    display: inline-block;
    cursor: pointer;

    .pl-color-button-background {
      background-size: 7px 7px;
      border-radius: 2px;
      box-sizing: border-box;
      height: 100%;
      width: 100%;

      .pl-color-button-color {
        height: 100%;
        width: 100%;
      }
    }
  }

  .pl-color-picker {
    @include sizeMixin(input) {
      .pl-color-button {
        height: $value/2;
        width: $value/2;
      }
    }
  }
}

@include theme {
  .pl-color-alpha-slider {
    width: 10px;
    background-size: 10px 10px;
    position: relative;
    cursor: pointer;
    border-radius: 10px;
    display: inline-block;

    .pl-color-alpha-shadow, .pl-color-alpha-thumb {
      pointer-events: none;
    }

    .pl-color-alpha-shadow {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      border-radius: 10px;
    }

    .pl-color-alpha-thumb {
      width: 10px;
      height: 10px;
      border-radius: 10px;
      border: solid 1px $colorInfo;
      display: inline-block;
      background-color: white;
      position: absolute;
      top: 0;
      left: 0;
      box-sizing: border-box;
    }
  }
}

@include theme {
  .pl-color-sv-panel {
    position: relative;
    box-shadow: 0 0px 4px 0 rgba(0, 0, 0, 0.4);
    cursor: pointer;
    display: inline-block;

    &:before, &:after, & span {
      pointer-events: none;
    }

    &, &:before, &:after {
      border-radius: 2px;
    }

    &:before, &:after {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      content: '';
    }

    &:before {
      background: linear-gradient(to right, white, rgba(255, 255, 255, 0));
    }

    &:after {
      background: linear-gradient(to top, black, rgba(255, 255, 255, 0));
    }

    .pl-color-sv-thumb {
      display: inline-block;
      position: absolute;
      width: 1px;
      height: 1px;
      left: 0;
      top: 0;
      z-index: 1;

      &:after {
        display: inline-block;
        width: 10px;
        height: 10px;
        border: solid 1px #999;
        border-radius: 10px;
        position: absolute;
        top: -5px;
        left: -5px;
        content: '';
        box-sizing: border-box;
        background-color: white;
      }
    }
  }
}

@include theme {
  .pl-color-hue-slider {
    background: linear-gradient(90deg, red 0, #ff0 17%, #0f0 33%, #0ff 50%, #00f 67%, #f0f 83%, red);
    display: inline-block;
    height: 10px;
    position: relative;
    cursor: pointer;
    border-radius: 10px;

    .pl-color-hue-slider-thumb {
      display: inline-block;
      height: 100%;
      background-color: white;
      position: absolute;
      top: 0;
      border: solid 1px $colorInfo;
      box-sizing: border-box;
      border-radius: 100px;
    }
  }
}

@include theme {
  .pl-cascade {
    .pl-input-suffix-icon .pl-icon {
      transform: rotate(90deg);
      transition: transform 300ms $transition;
    }

    &.pl-cascade-open {
      .pl-input-suffix-icon .pl-icon {
        transform: rotate(-90deg);
      }
    }
  }
}

@include theme {
  .pl-cascade-panel {
    display: inline-block;
    height: 200px;
    border: solid 1px $ibc;
    border-radius: $popperRadius;
    color: $itc;

    & > .pl-list {
      height: 100%;
      overflow: hidden;
      white-space: nowrap;
    }

    .pl-cascade-list {
      width: 180px;
      height: 100%;
      padding: 6px 0;
      font-size: 14px;
      display: inline-block;
      vertical-align: top;

      &:not(:last-child) {
        border-right: solid 1px $ibc;
      }

      .pl-list {
        overflow: hidden;
      }

      .pl-cascade-item {
        padding: 5px 0 5px 12px;
        font-weight: 400;
        font-size: 13px;
        color: $itc;
        background-color: white;
        transition: all 300ms $transition;
        cursor: pointer;

        .pl-cascade-content {
          padding-right: 30px;
          position: relative;

          .pl-cascade-arrow {
            position: absolute;
            top: 0;
            bottom: 0;
            right: 0;
            width: 30px;
            display: flex;
            align-items: center;
            justify-content: center;
            color: $icc;
          }
        }

        &.pl-cascade-item-expand, &:hover {
          color: $ihc;
          background-color: $disabled;
        }

        &.pl-cascade-item-active {
          color: $colorPrimary;
          background-color: rgba($colorPrimary, 0.1);
        }

        &.pl-cascade-empty {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;

          padding-left: 0;
          font-size: 12px;
          color: $disabledText;
          background-color: transparent;
          cursor: auto;

          .pl-icon {
            margin-bottom: 8px;
            font-size: 20px;
          }
        }

        &.pl-cascade-item-disabled {
          background-color: transparent;
          color: $disabledText;
          cursor: not-allowed;
        }
      }

      &.pl-cascade-filter-list {
        width: 360px;
      }
    }

    .pl-loading-mask {
      top: 1px;
      bottom: 1px;
      left: 1px;
      right: 1px;
    }
  }
}

@include theme {
  [service-name=pl-popper-service-cascade] {
    .pl-popper-content-inner {
      padding: 0 !important;
    }

    .pl-cascade-panel {
      border: none !important;
    }
  }
}

@include theme {
  .pl-tree {
    width: 100%;
    position: relative;

    .pl-tree-placeholder {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      height: 300px;
      display: flex;
      align-items: center;
      justify-content: center;
      flex-direction: column;
      color: $icc;

      & > .pl-icon {
        font-size: 3em;
        margin-bottom: 8px;
      }
    }

    .pl-tree-node-list {
      overflow: hidden;
    }

    .pl-tree-node {
      width: 100%;
      display: flex;
      align-items: center;
      justify-content: flex-start;
      font-size: 14px;
      box-sizing: border-box;
      border-bottom: solid 1px $ibl;
      color: $itc;

      & > .pl-tree-node-content {
        display: flex;
        align-items: center;
        justify-content: flex-start;
        flex: 1;
      }

      & > .pl-tree-node-operator {
        display: flex;
        align-items: center;
        font-size: 1.2em;

        .pl-tree-node-expander {
          width: 1em;
        }

        & > * {
          margin-right: 8px;
          color: $colorPrimary;
          user-select: none;
          width: 1em;
          transition: all 300ms linear;
        }

        .pl-icon {
          cursor: pointer;

          &.pl-tree-node-draggier {
            cursor: move;
          }
        }
      }

      &:first-child {
        border-top: solid 1px $ibl;
      }

      &:hover {
        background-color: #f6f6f6;
      }

      &.pl-tree-node-current {
        background-color: rgba($colorPrimary, 0.1);
        color: $colorPrimary;
      }

      &.pl-tree-empty-node {
        color: $colorWarn;

        & > .pl-tree-node-operator > * {
          color: $colorWarn;
        }
      }

      &.pl-tree-node-not-checkable {
        color: $disabledText;

        & > .pl-tree-node-operator > * {
          color: $disabledText;
        }
      }
    }
  }
}

@include theme {

  .pl-select-panel {

    .pl-select-panel-empty-text {
      color: $disabledText;
      font-size: 12px;
      height: 60px;
      display: flex;
      align-items: center;
      justify-content: center;
      flex-direction: column;

      .pl-icon {
        font-size: 20px;
        margin-bottom: 8px;
      }
    }

    &.pl-select-panel-multiple {
      .pl-select-option {
        .pl-select-option-checkbox {
          margin-right: 6px;
        }

        &.pl-select-option-selected {
          background-color: $colorPrimaryLight;
          color: $colorPrimary;
        }
      }
    }
  }

  .pl-select-option {

    &.pl-select-option-show {
      padding: 5px 12px;
      font-weight: 400;
      font-size: 14px;
      line-height: 22px;
      color: $itc;
      background-color: white;
      transition: background-color linear 200ms;
    }

    &.pl-select-option-group-child {
      padding-left: 28px;
    }

    &.pl-select-group {
      font-size: 12px;
      color: $colorPrimary;
      padding: 0 12px;
      margin: 4px 0;

      .pl-icon {
        margin-right: 6px;
      }
    }

    .pl-select-option-icon {
      margin-right: 6px;
      opacity: 0.5;
    }

    &:hover, &.pl-select-option-highlight {
      text-decoration: underline;

      &:not(.pl-select-option-disabled):not(.pl-select-group):not(.pl-select-option-selected) {
        background-color: #f2f2f2;
        cursor: pointer;
      }
    }

    &.pl-select-option-disabled {
      color: $disabledText;
      cursor: not-allowed;
    }

    &.pl-select-option-selected {
      background-color: rgba($colorPrimary, 0.1);
      color: $colorPrimary;
    }

  }

}

@include theme {
  .pl-time {
    .pl-time-inner {
      display: flex;
      align-items: stretch;
      height: 100%;

      & > span {
        color: $icc;
      }

      .pl-time-input-inner {
        border: none;
        outline: none;
        width: 90px;
        height: 100%;
        background: transparent;
      }
    }

    &.pl-time-range {
      .pl-time-input-inner {
        text-align: center;
      }
    }

    .pl-input-inner {
      width: auto !important;
    }
  }

  .pl-time-panel-foot {
    padding: 8px 8px 0 8px;
    border-top: solid 1px $ibl;
    display: flex;
    justify-content: center;

    & > * + * {
      margin-left: 8px;
    }
  }
}

@include theme {
  .pl-time-range-panel {
    display: inline-block;

    .pl-time-base-panel {

      &:first-child {
        border-right: solid 1px $ibc;
        border-radius: 0;
      }
    }
  }
}

$time-size: 24px;

@include theme {
  .pl-time-base-panel {
    display: inline-block;
    position: relative;
    color: $itc;

    &:before {
      position: absolute;
      top: $time-size*3;
      left: 0;
      right: 0;
      height: 1px;
      background-color: $ibl;
      content: '';
      pointer-events: none;
    }

    &:after {
      position: absolute;
      top: $time-size*4;
      left: 0;
      right: 0;
      height: 1px;
      background-color: $ibl;
      content: '';
      pointer-events: none;
    }

    .pl-time-base-column {
      border: none;

      & + .pl-time-base-column {
        border-left: dashed 1px $ibc;
      }
    }
  }
}

$time-size: 24px;

@include theme {
  .pl-time-base-column {
    display: inline-block;
    height: 7*$time-size;
    width: 40px;
    border-radius: 2px;

    .pl-time-base-column-list {
      margin: 0;
      padding: 0;
      list-style: none;
      position: relative;

      .pl-time-base-column-item {
        font-size: 12px;
        height: $time-size;
        line-height: $time-size;
        transition: all 300ms $transition;
        text-align: center;

        &.pl-time-base-column-option-item {
          cursor: pointer;

          &:hover {
            background-color: rgba($colorInfo, 0.1);
            color: $ihc;
          }
        }

        &.pl-time-base-column-item-disabled {
          color: $disabledText;
          cursor: not-allowed;

          &:hover {
            color: $disabledText;
            background-color: transparent;
          }
        }

        &.pl-time-base-column-item-current {
          background-color: rgba($colorPrimary, 0.1);
          color: $colorPrimary;

          &:hover {
            color: $colorPrimary;
          }
        }
      }
    }
  }
}

@include theme {
  $width: 276px;

  .pl-date-base-panel {

    border: solid 1px $ibc;
    border-radius: 2px;
    display: inline-block;
    padding: 8px 12px 12px 12px;
    width: 302px;
    height: 282px;
    transition: all $transition 500ms;
    box-sizing: border-box;

    .pl-date-base-panel-header {
      width: $width;
      box-sizing: border-box;
      display: flex;
      align-items: center;
      color: $itc;
      height: 40px;
      font-size: 14px;

      & > div:nth-child(2) {
        flex: 1;
        text-align: center;

        span {
          transition: all $transition 300ms;

          &:hover:not(.pl-date-base-panel-header-static-label) {
            color: $colorPrimary;
            cursor: pointer;
            text-decoration: underline;
          }
        }
      }

      .pl-button {
        color: $icc !important;

        &:hover {
          color: $colorPrimary !important;
        }
      }
    }

    .pl-date-base-panel-body {
      display: flex;
      align-items: center;
      justify-content: center;
      width: $width;
      height: 220px;
    }

    ul {
      margin: 0;
      padding: 0;
      list-style: none;
      display: block;

      .pl-date-base-panel-item {
        cursor: pointer;
        box-sizing: border-box;
        display: inline-block;
        font-size: 12px;
        color: $itc;
        user-select: none;

        & > div {
          width: 100%;
          height: 100%;
          padding: 4px;
          box-sizing: border-box;
          border-radius: 100px;

          span {
            display: inline-flex;
            width: 100%;
            height: 100%;
            border-radius: 100px;
            align-items: center;
            justify-content: center;
          }
        }

        &:hover:not(.pl-date-base-panel-item-disabled) {

          & > div {
            background-color: rgba($colorPrimary, 0.1);
          }
        }

        &.pl-date-base-panel-item-now {
          color: $colorPrimary;
          font-weight: bold;
        }

        &.pl-date-base-panel-item-active {
          & > div {
            background-color: rgba($colorPrimary, 0.1);

            & > span {
              background-color: $colorPrimary;
              color: white;
            }
          }
        }

        &.pl-date-base-panel-item-disabled {
          cursor: not-allowed;
          color: $disabledText;
        }

        &.pl-date-base-panel-item-hover {

          & > div {
            background-color: rgba($colorPrimary, 0.1);
            border-radius: 0;
          }
        }

        &.pl-date-base-panel-item-hover-start {

          & > div {
            background-color: rgba($colorPrimary, 0.1);
            border-radius: 100px 0 0 100px;
          }
        }

        &.pl-date-base-panel-item-hover-end {

          & > div {
            background-color: rgba($colorPrimary, 0.1);
            border-radius: 0 100px 100px 0;
          }

          &.pl-date-base-panel-item-hover-start {
            & > div {
              border-top-left-radius: 100px;
              border-bottom-left-radius: 100px;
            }

          }
        }
      }
    }

  }

  .pl-date-base-panel-year {
    .pl-date-base-panel-body {
      position: relative;
      overflow: hidden;
    }

    .pl-date-base-panel-year-list {
      transition: all $transition 500ms;
      position: absolute;
      left: 0;
      top: 0;
      right: 0;
      bottom: 0;
      display: flex;
      align-items: center;
      justify-content: center;
      flex-wrap: wrap;

      .pl-date-base-panel-year-item {
        width: 63px;
        height: 36px;
      }
    }
  }

  .pl-date-base-panel-month-wrapper {
    display: inline-block;
    position: relative;
    overflow: hidden;

    .pl-date-base-panel-month {

      .pl-date-base-panel-body {
        position: relative;
        overflow: hidden;
      }

      .pl-date-base-panel-month-item {
        height: 36px;
      }

      .pl-date-base-panel-month-quarter-list, .pl-date-base-panel-month-list {
        position: absolute;
        top: 0;
        bottom: 0;
        display: flex;
        align-items: center;
        justify-content: center;
        flex-wrap: wrap;
        transition: all $transition 500ms;
      }

      .pl-date-base-panel-month-quarter-list {
        left: 0;
        width: 56px;
        border-right: dashed 1px $ibc;

        .pl-date-base-panel-month-item {
          width: 36px;

          & > div {
            border-radius: 100px;
          }
        }
      }

      .pl-date-base-panel-month-list {
        right: 0;

        .pl-date-base-panel-month-item {
          width: 92px;
        }

        &.pl-date-base-panel-month-list-show-quarter {
          width: 216px;
          left: initial;

          .pl-date-base-panel-month-item {
            width: 72px;

            & > div > span {
              background-color: transparent;
              color: inherit;
            }
          }
        }
      }
    }

    padding: 0;

    & > div {
      border: none;
      position: absolute;
      left: 0;
      top: 0;
    }
  }

  .pl-date-panel-quarter {
    .pl-date-base-panel-month-item {
      &:nth-child(3n+1) {
        & > div {
          border-top-left-radius: 100px !important;
          border-bottom-left-radius: 100px !important;

          &:before {
            display: none;
          }
        }
      }

      &:nth-child(3n) {
        & > div {
          border-top-right-radius: 100px !important;
          border-bottom-right-radius: 100px !important;

          &:after {
            display: none;
          }
        }
      }
    }
  }

  .pl-date-base-panel-date-wrapper {
    display: inline-block;
    position: relative;
    overflow: hidden;

    .pl-date-base-panel-date {

      .pl-date-base-panel-body {
        flex-direction: column;
      }

      .pl-date-base-panel-date-time-label {
        margin-left: 8px;
      }

      ul {
        $itemWidth: 36px;
        $itemHeight: 30px;

        &.pl-date-base-panel-date-week-list {
          border-bottom: solid 1px $ibl;
          padding-bottom: 4px;
          width: $itemWidth*7;

          .pl-date-base-panel-date-week-item {
            width: $itemWidth;
            height: $itemHeight;
            padding: 0 3px;
          }
        }

        &.pl-date-base-panel-date-list {
          width: $itemWidth*7;

          .pl-date-base-panel-date-item {
            width: $itemWidth;
            height: $itemHeight;
            text-align: center;

            & > div {
              width: $itemHeight;
              height: $itemHeight;
              display: inline-block;
              padding: 3px;
            }

            &, & span {
              transition: all $transition 300ms;
            }

            &.pl-date-base-panel-date-item-other-month:not(:hover) {
              span {
                opacity: 0.2;
              }
            }

            &.pl-date-base-panel-item-disabled {
              & > div {
                span {
                  position: relative;

                  &:after {
                    position: absolute;
                    left: 5px;
                    top: 5px;
                    bottom: 0;
                    width: 1px;
                    content: '';
                    background-color: $ibc;
                    transform: rotate(-45deg);
                    transform-origin: top left;
                  }
                }
              }
            }

            margin-top: 2px;

            &.pl-date-base-panel-item-hover-start {
              & > div {
                position: relative;

                &:after {
                  position: absolute;
                  top: 0;
                  bottom: 0;
                  right: -3px;
                  width: 3px;
                  background-color: rgba($colorPrimary, 0.1);
                  content: '';
                }
              }
            }

            &.pl-date-base-panel-item-hover {
              & > div {
                position: relative;

                &:after {
                  position: absolute;
                  top: 0;
                  bottom: 0;
                  right: -3px;
                  width: 3px;
                  background-color: rgba($colorPrimary, 0.1);
                  content: '';
                }

                &:before {
                  position: absolute;
                  top: 0;
                  bottom: 0;
                  left: -3px;
                  width: 3px;
                  background-color: rgba($colorPrimary, 0.1);
                  content: '';
                }
              }
            }

            &.pl-date-base-panel-item-hover-end {
              & > div {
                position: relative;

                &:before {
                  position: absolute;
                  top: 0;
                  bottom: 0;
                  left: -3px;
                  width: 3px;
                  background-color: rgba($colorPrimary, 0.1);
                  content: '';
                }
              }

              &.pl-date-base-panel-item-hover-start {
                & > div {
                  position: relative;

                  &:before, &:after {
                    display: none;
                  }
                }
              }
            }
          }
        }
      }
    }

    .pl-date-base-panel-body {
      & > .pl-time-panel {
        border: none;
      }
    }

    padding: 0;

    & > div {
      border: none;
      position: absolute;
      left: 0;
      top: 0;
    }
  }

  .pl-date-panel-date-range {
    width: auto;
    padding: 0;
    white-space: nowrap;

    & > .pl-date-base-panel {
      border: none;
      white-space: normal;

      &:first-child {
        &:after {
          top: 16px;
          bottom: 16px;
          right: 0;
          content: '';
          position: absolute;
          background-color: $ibl;
          width: 1px;
        }
      }
    }
  }

  .pl-date-panel-week {
    .pl-date-base-panel-date-item {
      &:nth-child(7n+1) {
        & > div {
          border-top-left-radius: 100px !important;
          border-bottom-left-radius: 100px !important;

          &:before {
            display: none;
          }
        }
      }

      &:nth-child(7n) {
        & > div {
          border-top-right-radius: 100px !important;
          border-bottom-right-radius: 100px !important;

          &:after {
            display: none;
          }
        }
      }
    }
  }

  [service-name=pl-popper-service-date] {
    .pl-popper-content-inner {
      padding: 0 !important;

      & > .pl-date-base-panel {
        border: none;
      }
    }
  }

  .pl-date {
    .pl-date-inner {
      display: flex;
      align-items: stretch;
      height: 100%;

      & > span {
        color: $icc;
      }

      .pl-date-input-inner {
        border: none;
        outline: none;
        width: 90px;
        height: 100%;
        background: transparent;
      }
    }

    &.pl-date-range {
      .pl-date-input-inner {
        text-align: center;
      }
    }
  }

  .pl-date-panel-foot {
    padding: 8px;
    border-top: solid 1px $ibl;
    display: flex;
    justify-content: center;
    & > * + * {
      margin-left: 8px;
    }
  }
}

@include theme {
  .pl-form {
    text-align: center;
    overflow-x: auto;
    padding: 12px 0;

    & > .pl-form-body {
      text-align: initial;
      display: inline-block;
      position: relative;
      justify-content: space-evenly;
      color: $itc;
      white-space: normal;
      flex-shrink: 0;
    }

    .pl-form-item {
      display: inline-flex;
      margin-bottom: 18px;
      vertical-align: top;
      box-sizing: border-box;

      .pl-form-item-label {
        line-height: normal;
        align-items: flex-start;
        padding-right: 16px;
      }

      .pl-form-item-body {
        display: flex;
        align-items: center;
        flex-wrap: wrap;
        position: relative;

        & > * {
          flex-shrink: 0;
        }
      }

      .pl-form-item-message {
        position: absolute;
        top: 100%;
        left: 0;
        color: $colorError;
        font-size: 12px;
      }

      .pl-form-item-suffix {
        padding-left: 10px;
        box-sizing: border-box;
      }

      &:not(.pl-form-item-static-width) {
        .pl-form-item-label {
          flex: 1;
          flex-shrink: 0;
          overflow: visible;
          white-space: nowrap;
        }
      }

      @each $align in (left, center, right) {
        &.pl-form-item-label-align-#{$align} {
          .pl-form-item-label {
            text-align: $align
          }
        }
      }
      @each $content in (flex-start, center, flex-end, space-between, space-around, space-evenly) {
        &.pl-form-item-content-align-#{$content} {
          .pl-form-item-body {
            justify-content: $content;
          }
        }
      }

      &.pl-form-item-required {
        .pl-form-item-label {
          &:before {
            content: '*';
            color: $colorError;
            position: relative;
            top: 2px;
            right: 12px;
            width: 0;
            display: inline-block;
          }
        }
      }

      @include sizeMixin(form-item) {
        .pl-form-item-label {
          font-size: 16px;
          line-height: $value;
        }
      }

      &.pl-form-item-size-large {
        .pl-form-item-label {
          font-size: 16px;
        }
      }

      &.pl-form-item-size-normal {
        .pl-form-item-label {
          font-size: 14px;
        }
      }

      &.pl-form-item-size-mini {
        .pl-form-item-label {
          font-size: 12px;
        }
      }
    }

    &.pl-form-inline {
      .pl-form-item {
        & + .pl-form-item {
          margin-left: 8px;
        }
      }
    }
  }

  .pl-form {
    .pl-form-item-body {

      /*input*/
      & > .pl-input, & > .pl-textarea {
        flex: 1;
        flex-shrink: 0;

        .pl-input-inner, .pl-textarea-inner {
          width: 100%;
        }

        &.pl-input-custom {
          .pl-input-inner {
            width: 100% !important;

            input {
              width: 100% !important;
            }
          }
        }
      }

      /*button*/
      & > .pl-button + .pl-button {
        margin-left: 8px;
      }

      & > .pl-button-group {
        & > .pl-button + .pl-button {
          margin-left: 0;
        }
      }

      /*checkbox and radio*/
      & > .pl-checkbox-group, & > .pl-radio-group {
        display: flex;
        align-items: center;
        flex-wrap: inherit;
        justify-content: inherit;

        & > * {
          flex-shrink: 0;
        }
      }
    }

    .pl-form-item {
      &.pl-form-item-size-large {
        .pl-form-item-body {
          & > .pl-checkbox-group, & > .pl-radio-group {
            padding-top: 8px;
            padding-bottom: 8px;
          }
        }
      }

      &.pl-form-item-size-normal {
        .pl-form-item-body {
          & > .pl-checkbox-group, & > .pl-radio-group {
            padding-top: 4px;
            padding-bottom: 4px;
          }
        }
      }

      &.pl-form-item-size-mini {
        .pl-form-item-body {
          & > .pl-checkbox-group, & > .pl-radio-group {
            padding-top: 4px;
            padding-bottom: 4px;
          }
        }
      }
    }
  }
}

@include theme {
  $tableHeadColor: rgba(0, 0, 0, 0.85);
  $tableHeadBackground: #f5f7fa;
  $tableHeadHoverBackground: #e4e7ef;
  $tableHeadBorderColor: $ibc;

  $tableBodyColor: rgba(0, 0, 0, 0.65);

  $tableRowHoverBackground: #f5f7fa;
  $tableRowCurrentBackground: mix($colorPrimary, white, 7.5%);

  $scrollbarSize: 12px;
  /*---------------------------------------scroll-------------------------------------------*/
  .pl-table, .pl-virtual-table {
    position: relative;

    .pl-scroll {
      .pl-scroll-content {
        min-width: 100%;
      }

      .pl-vertical-scrollbar-wrapper, .pl-horizontal-scrollbar-wrapper {
        z-index: 10;
      }
    }

    &:hover {
      .pl-scroll {
        & > .pl-horizontal-scrollbar-wrapper, & > .pl-vertical-scrollbar-wrapper {
          .pl-vertical-scrollbar, .pl-horizontal-scrollbar {
            opacity: 1;
          }
        }
      }
    }
  }
  /*---------------------------------------虚拟表格-------------------------------------------*/
  .pl-virtual-table {
    height: 100%;
    width: 100%;
    overflow: hidden;

    .pl-virtual-table-summary-table-wrapper {
      pointer-events: none;
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      overflow: hidden;
      z-index: 5;

      & > table {
        &:after {
          content: '';
          display: block;
          height: $scrollbarSize;
          width: 1px;
        }
      }

      &:after {
        position: absolute;
        bottom: 0;
        height: $scrollbarSize;
        left: 0;
        content: "";
        background-color: #f5f7fa;
        z-index: 3;
        width: 5000px;
      }

      .pl-virtual-table-summary-table {
        pointer-events: auto;
        position: absolute;
        left: 0;

        td {
          background-color: $tableHeadBackground;
        }
      }
    }
  }
  /*---------------------------------------basic-------------------------------------------*/
  table {
    border-collapse: collapse;
    border-spacing: 0;
    box-sizing: border-box;
    table-layout: fixed;
    /**
     * table一直都是有个最小宽度100%。
     * 当总列宽小于表格宽度时，table没有具体width宽度，fit列不设置库宽度，使其自适应宽度；
     * 当总列宽大于表格宽度时，table设置具体的width宽度，fit值无效，以列具体宽度为准；
     */
    //min-width: 100%;  // 不能设置min-width，当文字过长的时候，会自动撑开列宽，导致表头与表体错位
    width: 100%;

    tr {
      td {
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        box-sizing: border-box;
        position: relative;
        padding: 1px 10px;
      }
    }
  }
  /*---------------------------------------collector-------------------------------------------*/
  .plc-collector {
    display: none;
  }
  /*---------------------------------------head-------------------------------------------*/
  .plt-head {
    td {
      background-color: $tableHeadBackground;
      color: $tableHeadColor;

      &:hover {
        background-color: $tableHeadHoverBackground;
      }

      .plt-head-cell-indicator {
        position: absolute;
        top: 0;
        bottom: 0;
        height: inherit;
        width: 6px;
        right: 0;
        content: '';
        background-color: rgba(black, 0.1);
        cursor: ew-resize;
        opacity: 0;
        transition: opacity 500ms $transition;
        z-index: 1;
      }
    }
  }
  /*---------------------------------------body-------------------------------------------*/
  .plt-body {
    tr {
      td {
        background-color: white;
        color: $tableBodyColor;
      }
    }
  }
  /*---------------------------------------row-------------------------------------------*/
  .plt-row {
    border-bottom: solid 1px $tableHeadBorderColor;

    &:hover {
      td {
        background-color: $tableRowHoverBackground;
      }
    }

    &.plt-row-current {
      &, &:hover {
        td {
          background-color: $tableRowCurrentBackground;
        }
      }
    }
  }
  .plt-head {
    tr {
      border-bottom: solid 1px $tableHeadBorderColor;
      border-top: solid 1px $tableHeadBorderColor;
    }
  }
  .plt-body {
    .pl-virtual-table-summary-table-wrapper {
      & > table {
        border-top: solid 1px $tableHeadBorderColor;
      }
    }
  }
  /*---------------------------------------cell-------------------------------------------*/
  .plt-cell {
    text-align: left;

    &.plt-cell-align-center {
      text-align: center;
    }

    &.plt-cell-align-right {
      text-align: right;
    }

    &.plt-cell-no-padding {
      padding: 0;
    }

    &.plt-cell-editing {
      &:not(.plt-cell-add-edit-padding) {
        padding-left: 0;
      }
    }
  }
  /*---------------------------------------border-------------------------------------------*/
  .pl-table {

    &.pl-table-border {
      tr {
        td {
          border-right: solid 1px $tableHeadBorderColor;
          border-left: solid 1px $tableHeadBorderColor;
        }
      }

      &:after {
        position: absolute;
        right: 0;
        bottom: 0;
        top: 0;
        left: 0;
        background-color: transparent;
        border: solid 0.5px $tableHeadBorderColor;
        content: '';
        z-index: 5;
        pointer-events: none;
      }
    }

    &.pl-table-stripe {
      .plt-body {
        .pl-virtual-list-content {
          table {
            tr:nth-child(odd):not(:hover):not(.plt-row-current) {
              td {
                background-color: #f2f5ff;
              }
            }
          }
        }
      }
    }
  }
  /*---------------------------------------shadow-------------------------------------------*/
  .pl-table {
    &, &.pl-table-border {

      .plt-cell-last-fixed-left, .plt-cell-first-fixed-right {

        &:before {
          position: absolute;
          top: 0;
          bottom: 0;
          width: 3px;
          pointer-events: none;
          content: '';
          z-index: 4;
          background-color: transparent;

          left: initial;
          right: initial;
          transform: none;
        }

        &.plt-cell-last-fixed-left {
          &:before {
            right: 0;
            box-shadow: inset 10px 0 10px -10px mix(black, $boxshadowColor, 10%);
          }
        }

        &.plt-cell-first-fixed-right {
          &:before {
            left: 0;
            box-shadow: inset -10px 0 10px -10px mix(black, $boxshadowColor, 10%);
          }
        }
      }
    }

    &.plt-table-hide-fixed-left .plt-cell-last-fixed-left, &.plt-table-hide-fixed-right .plt-cell-first-fixed-right {
      &:before {
        display: none;
      }
    }
  }
  /*---------------------------------------standard-------------------------------------------*/
  /*plc-index*/
  .plc-index-summary-text {
    font-size: 12px;
  }
  /*input*/
  .plt-cell {
    & > * {
      vertical-align: middle;
    }

    & > .pl-input {
      width: 100%;

      & > .pl-input-inner {
        width: inherit !important;

        & > .pl-input-custom-inner {
          & > input {
            width: 100% !important;
          }
        }
      }
    }
  }
  /*plc-expand*/
  .plt-row {
    &.plt-expand-row {
      td {
        background-color: white;
        z-index: 5;
        white-space: normal;
        padding: 0;
        overflow: unset;
      }

      .plt-expand-body {
        padding: 16px;
        box-sizing: border-box;
        position: sticky;
        left: 0;

        & > .pl-form {
          padding: 0;
        }
      }
    }

    .plc-expand-icon {
      .pl-icon {
        @include transition;
        transition-property: transform;
      }

      &.plc-expand-icon-active {
        .pl-icon {
          transform: rotate(-180deg);
        }
      }
    }
  }
  /*plc-draggier*/
  .pl-button.plc-draggier-handler {
    cursor: move;
  }
  /*col draggier*/
  .plt-col-draggier-indicator {
    &[droppable="true"] {
      background-color: $colorPrimary;
    }

    &[droppable="false"] {
      background-color: $colorError;
    }
  }
  /*plc-tree*/
  .plc-tree-node {
    white-space: nowrap;

    & > * {
      display: inline-block;
      height: 100%;
    }

    .plc-tree-node-expander, .plc-tree-node-check {
      width: 30px;
      text-align: center;
    }
  }
  .plc-tree-node-content {
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    padding-left: 8px;
    box-sizing: border-box;
  }
}

@include theme {
  .pl-upload {

    .pl-upload-item {
      position: relative;

      .pl-upload-item-inner {
        padding: 6px 9px;
        font-size: 12px;
        color: $itc;
        position: relative;
        z-index: 2;

        & > .pl-icon:first-child {
          color: $icc;
        }

        & > .pl-icon, & > .pl-loading {
          margin-right: 8px;
        }
      }

      .pl-upload-item-progress {
        position: absolute;
        top: 2px;
        bottom: 2px;
        left: 2px;
        right: 2px;
        z-index: 1;

        .pl-upload-item-progress-inner {
          transition: all 1s linear;
          height: 100%;
          background-color: rgba($colorPrimary, 0.2);
          border-top-right-radius: 100px;
          border-bottom-right-radius: 100px;
          z-index: -1;
        }
      }

      .pl-upload-item-content {
        display: inline-block;
      }

      & + .pl-upload-item {
        margin-top: 8px;
      }

      &.pl-upload-item-status-success {
        .pl-upload-item-inner {
          &, & > .pl-icon {
            color: $colorPrimary;
          }
        }
      }

      &.pl-upload-item-status-error {
        .pl-upload-item-inner {
          &, & > .pl-icon {
            color: $colorError;
          }
        }
      }

      &.pl-upload-item-status-ready {
        .pl-upload-item-inner {
          &, & > .pl-icon {
            color: $colorWarn;
          }
        }
      }
    }

    &.pl-upload-preview {
      .pl-upload-item {
        .pl-upload-item-content {
          &:hover {
            text-decoration: underline;
            color: $colorPrimary;
            cursor: pointer;
          }
        }
      }
    }

    &.pl-upload-remove {
      .pl-upload-item {
        .pl-upload-item-inner {
          padding-right: 32px;
        }

        .pl-upload-item-remove {
          width: 32px;
          position: absolute;
          top: 0;
          right: 0;
          bottom: 0;
          display: flex;
          align-items: center;
          justify-content: center;
          cursor: pointer;
          color: $icc;
          transition: all 300ms linear;

          &:hover {
            background-color: $colorError;

            .pl-icon {
              color: white;
            }
          }
        }
      }
    }

    &.pl-upload-single {
      display: flex;
      flex-direction: row;
      align-items: stretch;
      justify-content: center;

      & > .pl-button-group:first-child {
        & > .pl-button:last-child {
          border-top-right-radius: 0;
          border-bottom-right-radius: 0;
        }
      }

      .pl-upload-item {
        flex: 1;
        border: solid 1px $ibc;
        border-left: none;
        border-top-right-radius: 2px;
        border-bottom-right-radius: 2px;
      }
    }

    &.pl-upload-multiple {
      .pl-upload-button {

      }

      .pl-upload-list {
        margin-top: 8px;

        .pl-upload-item {
          border: dashed 1px $ibc;
        }
      }

      .pl-upload-drop-area {
        display: flex;
        flex-direction: column;
        padding: 12px;
        align-items: center;
        justify-content: center;
        border: dashed 1px $ibc;
        cursor: pointer;

        & > .pl-icon {
          font-size: 32px;
          color: $colorPrimary;
          margin-bottom: 8px;
        }

        .pl-upload-drop-upload-button {
          margin-left: 8px;
        }
      }

      &.pl-upload-disabled {
        .pl-upload-drop-area {
          color: $disabledText;

          & > .pl-icon {
            color: $icc;
          }
        }
      }
    }


  }
}

@include theme {
  .pl-nav {
    border: solid 1px $ibc;
  }
}

.pl-image-preview-service {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(black, 0.85);
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 300ms linear;

  .pl-image-preview-service-indicator {

    position: absolute;
    bottom: 80px;
    left: 0;
    right: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: transform linear 300ms;
    user-select: none;

    .pl-image-preview-service-indicator-item {
      width: 16px;
      height: 4px;
      background-color: rgba(white, 0.25);
      transition: all linear 150ms;

      & + div {
        margin-left: 8px;
      }

      &.pl-image-preview-service-indicator-item-active {
        background-color: white;
        width: 32px;
      }
    }
  }

  .pl-image-preview-service-button-group {
    position: absolute;
    bottom: 20px;
    left: 0;
    right: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: transform linear 250ms;
    user-select: none;

    .pl-image-preview-service-button {
      height: 48px;
      width: 48px;
      background-color: rgba(white, 0.45);
      display: flex;
      align-items: center;
      justify-content: center;
      margin-right: 1px;
      font-size: 24px;

      &:first-child {
        padding-left: 5px;
        border-top-left-radius: 100px;
        border-bottom-left-radius: 100px;
      }

      &:last-child {
        padding-right: 5px;
        border-top-right-radius: 100px;
        border-bottom-right-radius: 100px;
      }

      &:hover {
        background-color: rgba(black, 0.45);
