      cursor: grab;
      position: relative;
      user-select: none;
      transition: transform linear 0.1s;
    }
  }
}

.pl-image-preview-enter-active, .pl-image-preview-leave-active {
  opacity: 1;
}

.pl-image-preview-enter-from, .pl-image-preview-leave-to {
  opacity: 0;

  .pl-image-preview-service-img-wrapper {
    transform: scale(0.25);
  }

  .pl-image-preview-service-button-group {
    transform: translateY(200px);
  }
}

@include theme {
  .pl-image-preview-service {
    .pl-image-preview-service-img-wrapper {
      transition: transform $transition 300ms;
    }
  }

  img.pl-image {
    vertical-align: bottom;
  }

  div.pl-image {
    vertical-align: bottom;
    min-height: 60px;
    min-width: 60px;
    display: inline-flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    .pl-icon {
      font-size: 36px;
      color: $icc;
    }

    span {
      color: $itc;
      font-size: 12px;
    }

    &.pl-image-status-pending {
      .pl-loading {
        margin-bottom: 0;
      }
    }

    &.pl-image-status-empty {
      background-color: $disabled;

      .pl-icon {
        color: $disabledDeep;
      }

      span {
        color: $disabledText;
      }
    }

    &.pl-image-status-error {
      background-color: rgba($colorError, 0.1);

      .pl-icon {
        color: $colorError;
      }

      span {
        color: $colorError;
      }
    }

    &.pl-image-status-pending {
      background-color: rgba($colorPrimary, 0.1);

      .pl-icon {
        color: $colorPrimary;
      }

      span {
        color: $colorPrimary;
      }
    }
  }

  img.pl-image-preview-on-click {
    cursor: zoom-in;
  }

  .pl-image-uploader {
    display: inline-flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    vertical-align: bottom;
    user-select: none;
    box-sizing: border-box;
    position: relative;
    overflow: hidden;
    cursor: pointer;

    & > img {
      height: 100%;
      width: 100%;
    }

    &, & .pl-icon, & span {
      transition: all linear 200ms;
    }

    .pl-icon {
      font-size: 32px;
    }

    span {
      font-size: 12px;
      margin-top: 4px;
    }

    & > div {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
    }

    &.pl-image-uploader-status-empty {
      border: dashed 2px $ibc;

      .pl-icon {
        color: $icc;
      }

      span {
        color: $itc;
      }

      &:hover {
        border-color: $colorPrimary;

        .pl-icon, span {
          color: $colorPrimary;
        }
      }

      &:active {
        background-color: rgba($colorPrimary, 0.1);
      }
    }

    &.pl-image-uploader-status-fail {
      .pl-image-uploader-fail {
        background-color: rgba(black, 0.85);
        color: white;
      }
    }
  }
}

@include theme {
  .pl-triangle {
    display: inline-block;
    vertical-align: middle;
    overflow: hidden;
    box-sizing: border-box;

    .pl-triangle-target {
      width: 0;
      height: 0;
      position: relative;
    }
  }
}

$waves: (
        large:6px,
        normal:4px,
        mini:2,
);

@each $key, $value in $waves {
  [plain-click-node-#{$key}] {
    &.plain-click-node, & .plain-click-node {
      position: relative;

      &:after {
        display: none;

        content: '';
        position: absolute;
        top: 1px;
        left: 1px;
        bottom: 1px;
        right: 1px;
        border-radius: inherit;
        border: 0 solid #12b4a5;
        opacity: 0.6;
        animation: fade-effect-#{$key} 2s cubic-bezier(0.08, 0.82, 0.17, 1), wave-effect-#{$key} 500ms cubic-bezier(0.08, 0.82, 0.17, 1);
        animation-fill-mode: forwards;
        pointer-events: none;
      }
    }

    &[plain-click-node-waving] {
      &.plain-click-node, & .plain-click-node {
        &:after {
          display: initial;
        }
      }
    }
  }

  @keyframes wave-effect-#{$key} {
    100% {
      top: -$value;
      left: -$value;
      bottom: -$value;
      right: -$value;
      border-width: 6px;
    }
  }

  @keyframes fade-effect-#{$key} {
    100% {
      opacity: 0;
    }
  }
}
