  }
}

@include theme {
  .pl-checkbox {
    @include public-style;
    cursor: pointer;
    user-select: none;
    position: relative;
    display: inline-block;
    vertical-align: baseline;
    outline: none;
    font-size: 14px;

    .plain-click-node {
      width: 1.1em;
      height: 1.1em;
      display: inline-block;
      vertical-align: -0.1em;

      .pl-checkbox-inner {
        transition: all 150ms linear;
        float: left;
      }

      & + .pl-checkbox-label {
        margin-left: 0.5em;
      }
    }

    .pl-checkbox-label {
      display: inline-block;
    }

    @include statusMixin(checkbox) {
      .plain-click-node {
        .pl-checkbox-inner {
          color: $value;
        }

        &:after {
          border-color: $value;
        }
      }
    }

    &.pl-checkbox-status-error {
      &.pl-checkbox-check-status-check {
        .pl-checkbox-label {
          color: $colorError;
        }
      }
    }

    &.pl-checkbox-size-large {
      font-size: 16px;
    }

    &.pl-checkbox-size-normal {
      font-size: 14px;
    }

    &.pl-checkbox-size-mini {
      font-size: 12px;
    }

    &:focus {
      .pl-checkbox-label {
        text-decoration: underline;
      }
    }

    &.pl-checkbox-disabled {
      cursor: not-allowed;

      .pl-checkbox-label {
        color: $disabledText !important;
      }

      .pl-checkbox-inner {
        border-color: currentColor !important;
        background-color: $disabled !important;
        color: $disabledText !important;
      }
    }
  }

  .pl-checkbox-group {
    width: 100%;
  }
}

@include theme {
  .pl-checkbox-inner {
    width: 1.1em;
    height: 1.1em;

    display: inline-block;
    box-sizing: border-box;
    vertical-align: -0.15em;
    border-width: 1px;
    border-style: solid;
    //border-radius: 2px;

    &.pl-checkbox-inner-status-check {
      border-color: currentColor;
      background-color: currentColor;

      .pl-checkbox-inner-check-polyline {
        fill: transparent;
        stroke: white;
        stroke-width: 10;
      }
    }

    &.pl-checkbox-inner-status-uncheck {
      border-color: $ipc;
      background-color: transparent;
    }

    &.pl-checkbox-inner-status-minus {
      border-color: currentColor;
      background-color: transparent;

      .pl-checkbox-inner-minus-polyline {
        fill: currentColor;
      }
    }

    &.pl-checkbox-inner-disabled {
      &.pl-checkbox-inner-status-check {
        background-color: transparent;

        .pl-checkbox-inner-check-polyline {
          stroke: currentColor;
        }
      }

      &.pl-checkbox-inner-status-minus {
        background-color: transparent;

        .pl-checkbox-inner-minus-polyline {
          stroke: currentColor;
        }
      }

    }
  }
}

