        flex-direction: column;

        .pl-form-item-label {
          text-align: left;
          padding-left: 0 !important;
        }
      }
    }

    &.pl-form-column-1 {
      display: inline-flex;
      flex-direction: column;

      .pl-form-item {
        justify-content: flex-end;
        position: relative;

        .pl-form-item-label {
          flex: 1;
          white-space: nowrap;
        }
      }

      &.pl-form-center-when-single-column {
        width: 100% !important;
        align-items: center;

        &:not(.pl-form-vertical-label) {
          .pl-form-item {
            .pl-form-item-label {
              position: absolute;
              right: 100%;
              top: 0;
              white-space: nowrap;
            }

            .pl-form-item-body {
              .pl-form-item-message {
                left: 100% !important;
                top: 0 !important;
                white-space: nowrap;
                padding-left: 8px;
              }
            }

            /*line-height*/
            @include sizeMixin(form-item) {
              .pl-form-item-message {
                line-height: $value !important;
              }
            }
          }
        }
      }
    }

    &:not(.pl-form-column-1) {
      .pl-form-item-label {
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
      }
    }
  }

  .pl-form {
    .pl-form-item-body {

      .pl-slider, .pl-toggle {
