            }
          }
        }
      }

      /*button*/
      & > .pl-button + .pl-button {
        margin-left: 8px;
      }

      & > .pl-button-group {
        & > .pl-button + .pl-button {
          margin-left: 0;
        }
      }

      /*checkbox and radio*/
      & > .pl-checkbox-group, & > .pl-radio-group {
        display: flex;
        flex-wrap: wrap;
        align-items: center;
        justify-content: inherit;

        & > * {
