      &::-webkit-outer-spin-button,
      &::-webkit-inner-spin-button {
        -webkit-appearance: none !important;
        margin: 0;
      }

      &:-webkit-autofill {
        background-color: white !important;
        -webkit-transition: background-color 10000s cubic-bezier(1, -100, 1, -100) 0s !important;
      }
    }

    &:not(.pl-input-disabled) {

      &.pl-input-focus {
        .pl-input-inner, .pl-textarea-inner {
          border-color: $colorPrimary;
          box-shadow: 0 0 0 2px rgba(18, 180, 165, 0.2);
        }
      }

      .pl-input-inner, .pl-textarea-inner {
        &:focus {
          border-color: $colorPrimary;
          box-shadow: 0 0 0 2px rgba(18, 180, 165, 0.2);
        }
      }

      .pl-input-inner, .pl-textarea-inner {
        &:hover {
          border-color: $colorPrimary;
        }
      }
    }

    @include shapeMixin(input) {
      .pl-input-inner, .pl-textarea-inner {
        border-radius: $value;
      }
    }

    @include sizeMixin(input) {
      &.pl-input-size-large {

        .pl-input-inner, .pl-textarea-inner, input {
          font-size: 16px;
        }

        .pl-input-inner, .pl-textarea-inner {
          padding-left: 12px;
          padding-right: 12px;
        }

        &.pl-input-shape-round {
          .pl-input-inner, .pl-textarea-inner {
            padding-left: 24px;
            padding-right: 24px;
          }
        }
      }
      &.pl-input-size-normal {

        .pl-input-inner, .pl-textarea-inner, input {
          font-size: 14px;
        }

        .pl-input-inner, .pl-textarea-inner {
          padding-left: 10px;
          padding-right: 10px;
        }

        &.pl-input-shape-round {
          .pl-input-inner, .pl-textarea-inner {
            padding-left: 20px;
            padding-right: 20px;
          }
        }
      }
      &.pl-input-size-mini {

        .pl-input-inner, .pl-textarea-inner, input {
          font-size: 12px;
        }

        .pl-input-inner, .pl-textarea-inner {
          padding-left: 8px;
          padding-right: 8px;
        }

        &.pl-input-shape-round {
          .pl-input-inner, .pl-textarea-inner {
            padding-left: 16px;
            padding-right: 16px;
          }
        }
      }
    }

    @include statusMixin(input) {
      &:not(.pl-input-disabled) {

        &.pl-input-focus {
          .pl-input-inner, .pl-textarea-inner {
            box-shadow: 0 0 0 2px mix($value, white, 20%);
          }
        }

        .pl-input-inner, .pl-textarea-inner {
          border-color: $value;
          color: $value;
          background-color: mix($value, white, 5%);

          &:focus {
            box-shadow: 0 0 0 2px mix($value, white, 20%);
          }
        }
      }
    }

    &.pl-input-disabled {
      .pl-input-inner, .pl-textarea-inner {
        background-color: $disabled;
        cursor: not-allowed;
        color: $disabledText;
      }
    }

    &.pl-input-block {
      width: 100%;
      display: block;

      .pl-input-inner, .pl-textarea-inner {
        width: 100%;
      }

    }

    &.pl-input-fill-group {
      flex: 1;

      & > .pl-input-inner {
        width: 100% !important;
      }

      &.pl-input-custom {
        .pl-input-inner, .pl-input-custom-inner, input {
          width: 100% !important;
        }
      }
    }


    /*align*/
    &.pl-input-align-left {
      .pl-input-inner, input {
        text-align: left
      }
    }

    &.pl-input-align-center {
      .pl-input-inner, input {
        text-align: center;
      }
    }

    &.pl-input-align-right {
      .pl-input-inner, input {
        text-align: right;
      }
    }

    /*---------------------------------------specific size-------------------------------------------*/

    &.pl-input {
      @include sizeMixin(input) {
        .pl-input-inner {
          height: $value;
        }
        div.pl-input-inner {
          line-height: $value - 2;
        }
      }
    }

    &.pl-textarea {
      position: relative;
      display: inline-block;

      .pl-textarea-inner {
        float: left;
      }

      @include sizeMixin(input) {
        &.pl-input-size-large {
          .pl-textarea-inner {
            padding-top: 12px;
            padding-bottom: 12px;
          }
        }
        &.pl-input-size-normal {
          .pl-textarea-inner {
            padding-top: 8px;
            padding-bottom: 8px;
          }
        }
        &.pl-input-size-mini {
          .pl-textarea-inner {
            padding-top: 4px;
            padding-bottom: 4px;
          }
        }
      }

      .pl-textarea-hidden {
        position: absolute;
        pointer-events: none;
        opacity: 0;
        top: 0;
        left: 0;
        width: 100%;
      }
    }
  }

  /*---------------------------------------input suffix prefix-------------------------------------------*/
  .pl-input {
    position: relative;

    .pl-input-suffix-icon, .pl-input-prefix-icon {
      user-select: none;
      cursor: pointer;
      position: absolute;
      top: 0;
      bottom: 0;
      box-sizing: border-box;
      text-align: center;
      color: $icc;
      font-size: 12px;
      transition: all 300ms linear;
      z-index: 2;

      display: flex;
      align-items: center;
      justify-content: center;
    }

    .pl-input-suffix-icon {
      right: 2px;
    }

    .pl-input-prefix-icon {
      left: 2px;
    }

    @include sizeMixin(input) {

      &.pl-input-suffix-padding {
        &.pl-input-size-large {
          .pl-input-inner {
            padding-right: #{12px*3};
          }

          .pl-input-suffix-icon {
            width: #{12px*3};
          }
        }

        &.pl-input-size-normal {
          .pl-input-inner {
            padding-right: #{10px*3};
          }

          .pl-input-suffix-icon {
            width: #{10px*3};
          }
        }

        &.pl-input-size-mini {
          .pl-input-inner {
            padding-right: #{8px*3};
          }

          .pl-input-suffix-icon {
            width: #{8px*3};
          }
        }
      }
      &.pl-input-prefix-padding {
        &.pl-input-size-large {
          .pl-input-inner {
            padding-left: #{12px*3};
          }

          .pl-input-prefix-icon {
            width: #{12px*3};
          }
        }

        &.pl-input-size-normal {
          .pl-input-inner {
            padding-left: #{10px*3};
          }

          .pl-input-prefix-icon {
            width: #{10px*3};
          }
        }

        &.pl-input-size-mini {
          .pl-input-inner {
            padding-left: #{8px*3};
          }

          .pl-input-prefix-icon {
            width: #{8px*3};
          }
        }
      }
    }


    &.pl-input-clear {
      &:not(.pl-input-suffix) {
        &.pl-input-empty {
          .pl-input-clear-icon {
            opacity: 0;
            pointer-events: none;
          }
        }
      }

      &.pl-input-suffix {
        .pl-input-clear-icon {
          opacity: 0;
          pointer-events: none;
        }

        &:not(.pl-input-empty) {
          &:hover:not(.pl-input-not-editable) {
            .pl-input-clear-icon {
              opacity: 1;
              pointer-events: auto;
            }

            .pl-input-suffix-icon:not(.pl-input-clear-icon) {
              opacity: 0;
              pointer-events: none;
            }
          }
        }
      }
    }

    .pl-loading {
      cursor: not-allowed;

      &:before {
        position: absolute;
        top: 2px;
        bottom: 2px;
        left: 2px;
        right: 2px;
        background-color: white;
        content: '';
      }
    }

    .pl-input-inner-hidden {
      position: fixed;
      float: left;
      z-index: -999;
      pointer-events: none;
      opacity: 0;
      display: none;
    }
  }

  .pl-input-container {
    width: 100%;
    display: table;

    .pl-input-prepend, .pl-input-append {
      background-color: #f5f7fa;
      color: $itc;
      vertical-align: baseline;
      display: table-cell;
      position: relative;
      border: 1px solid #dcdfe6;
      border-radius: 2px;
      padding: 0 12px;
      overflow: hidden;

      width: 1px;
      white-space: nowrap;

      &.pl-input-prepend {
        border-right: none;
      }

      &.pl-input-append {
        border-left: none;
      }

      & > .pl-input, & > .pl-button {
        display: inline-block;
        margin: -1px -13px -1px -12px;
      }

      & > .pl-input {
        .pl-input-inner {
          border: none !important;
        }
      }
    }

    .pl-input {
      display: table-cell;
      position: relative;
      z-index: 1;

      .pl-input-inner {
        width: 100%;
      }
    }

    & > *:not(:last-child):not(:first-child) {
      border-radius: 0;

      &.pl-input {
        .pl-input-inner {
          border-radius: 0;
        }
      }
    }

    & > *:first-child {
      border-top-right-radius: 0;
      border-bottom-right-radius: 0;

      &.pl-input {
        .pl-input-inner {
          border-top-right-radius: 0;
          border-bottom-right-radius: 0;
        }
      }
    }

    & > *:last-child {
      border-top-left-radius: 0;
      border-bottom-left-radius: 0;

      &.pl-input {
        .pl-input-inner {
          border-top-left-radius: 0;
          border-bottom-left-radius: 0;
        }
      }
    }
  }

  .pl-input-custom {
    .pl-input-inner {
      width: auto !important;
    }

    .pl-input-custom-inner {
      display: flex;
      align-content: stretch;
      height: 100%;

      & > span {
        color: $icc;
      }

      .pl-input-custom-inner-input {
        border: none;
        outline: none;
        width: 90px;
        height: 100%;
        background: transparent;
      }

      &[range=true] {
        .pl-input-custom-inner-input {
          text-align: center;
        }
      }
    }
  }

  .pl-form-item-content {
    .pl-input-custom {
      .pl-input-custom-inner {
        display: flex;
        align-items: center;

        .pl-input-custom-inner-input {
          flex: 1;
          font-size: inherit;
          color: inherit;
        }
      }

      &.pl-input-disabled {
        .pl-input-custom-inner-input {
          cursor: not-allowed;
        }
      }
    }
  }

  .pl-input {
    &.pl-input-tags {
      user-select: none;
      vertical-align: top;

      .pl-input-inner {
        padding-left: 3px !important;
        height: auto !important;
        line-height: normal !important;
        cursor: pointer;
      }

      .pl-input-inner-tags {
        .pl-input-inner-tag-item {
          display: inline-block;
          font-size: 12px;
          border: solid 1px $ibl;
          background-color: #fafafa;
          padding: 0 6px;
          border-radius: 2px;
          margin: 3px;
          color: inherit;

          span + .pl-icon {
            margin-left: 3px;
            color: $icc;
          }

          &.pl-input-inner-tag-item-takeover {
            display: inline-block;
            padding: 0 0;
            margin-left: 0;
            margin-right: 0;
            width: 4px;
            opacity: 0;
          }
        }
      }

      @include sizeMixin(input) {
        .pl-input-inner {
          min-height: $value;

          .pl-input-inner-tags {
            .pl-input-inner-tag-item {
              line-height: $value - 9;
            }
          }
        }
      }

      &.pl-input-disabled {
        .pl-input-inner-tag-item {
          cursor: not-allowed;
        }
      }
    }
  }
}

.pl-input-group {
  display: inline-block;
  white-space: nowrap;

  & > * {
    display: inline-block;

    &.pl-input-group.pl-input-group-block {
      display: inline-flex;
    }
  }

  &.pl-input-group-block {
    width: 100%;
    display: flex;
    align-items: center;
    flex-wrap: nowrap;
  }

  & > *:not(:first-child) {
    border-top-left-radius: 0 !important;
    border-bottom-left-radius: 0 !important;

    input, .pl-input-inner {
      border-top-left-radius: 0 !important;
      border-bottom-left-radius: 0 !important;
    }
  }

  & > *:not(:last-child) {
    border-top-right-radius: 0 !important;
    border-bottom-right-radius: 0 !important;

    input, .pl-input-inner {
      border-top-right-radius: 0 !important;
      border-bottom-right-radius: 0 !important;
    }
  }

  & > .pl-button {
    position: relative;
    z-index: 2;
  }

  & > *, & > * input {
    &:hover, &:focus, &.pl-input-focus {
      position: relative;
      z-index: 1;
    }
  }

  @for $i from 2 through 10 {
    & > *:nth-child(#{$i}) {
      margin-left: -#{$i - 1}px;
    }
  }
}

@include theme {
    .pl-loading {
        @include statusMixin(loading) {
            color: $value;
        }
    }
}

@include theme {
  .pl-loading-mask {
    color: $colorPrimary;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(255, 255, 255, 0.5);
    transition: all linear 300ms;
    transform-origin: center center;
    cursor: progress;
    pointer-events: auto;

    .pl-loading {
      margin-bottom: 20px;
      font-size: 32px;

      & + span {
        font-size: 14px;
      }
    }

    &.pl-loading-mask-unlock {
      pointer-events: none;
    }

    &.pl-loading-mask-fixed-position {
      position: fixed;
    }
  }
}

.pl-transition-loading-mask-enter-active, .pl-transition-loading-mask-leave-active {
  opacity: 1;
}

.pl-transition-loading-mask-enter-from, .pl-transition-loading-mask-leave-to {
  opacity: 0;
}

@include theme {
  .pl-item {
    transition: all 300ms $transition;
    @include public-style;
  }

  .pl-list {
    display: block;
    width: 100%;
    position: relative;
    @include public-style;

    .pl-item-block {
      display: block;
      width: 100% !important;
    }
  }

  $directions: (
          top:(x:0, y:-30%),
          left:(x:-80%, y:0),
          right:(x:80%, y:0),
          bottom:(x:0, y:30%),
          left-top:(x:-80%, y:-30%),
          top-left:(x:-80%, y:-30%),
          right-top:(x:80%, y:-30%),
          top-right:(x:80%, y:-30%),
          left-bottom:(x:-80%, y:30%),
          bottom-left:(x:-80%, y:30%),
          right-bottom:(x:80%, y:30%),
          bottom-right:(x:80%, y:30%),
  );

  @each $key, $value in $directions {

    .pl-list-move-#{$key}-enter-from,
    .pl-list-move-#{$key}-leave-to {
      opacity: 0;
      transform: translateX(map_get($value, x)) translateY(map_get($value, y));
    }

    .pl-list-move-#{$key}-leave-active, .pl-list-move-#{$key}-leave-to {
      position: absolute !important;
    }
  }

  .pl-list-move-scale-enter-from,
  .pl-list-move-scale-leave-to {
    opacity: 0;
    transform: scale(0.8);
  }

  .pl-list-move-scale-leave-active, .pl-list-move-scale-leave-to {
    position: absolute !important;
  }
}

@include theme {

  .pl-number {
    display: inline-block;
    width: initial;

    .pl-input-inner {
      position: relative;
      padding-top: 1px;
      padding-bottom: 1px;
