$screen-sm: 768px;
$screen-md: 992px;
$screen-lg: 1200px;
$screen-xl: 1920px;
$cell-num: 24;
$flexs: (
  start:(justify-content:flex-start),
  center:(justify-content:center),
  end:(justify-content:flex-end),
  space-between:(justify-content:space-between),
  space-around:(justify-content:space-around),
  top:(align-items:flex-start),
  middle:(align-items:center),
  bottom:(align-items:flex-end),
);
$screens: (
  xs:(max-width:$screen-sm),
  sm:(min-width:$screen-sm),
  md:(min-width:$screen-md),
  lg:(min-width:$screen-lg),
  xl:(min-width:$screen-xl),
);

@mixin res($key, $map: $screens) {
  // 循环断点Map，如果存在则返回
  @if map-has-key($map, $key) {
    @media only screen and #{inspect(map-get($map, $key))} {
      @content;
    }
  } @else {
    @warn "Undefeined points: `#{$map}`";
  }
}

@include theme {
  .pl-row {
    height: auto;
    position: relative;
    display: block;
    margin-right: 0 !important;

    &:not(:last-child) {
      margin-bottom: 16px;
    }

    &, & * {
      box-sizing: border-box;
    }

    /*flex布局*/
    &.pl-row {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;

      &:before,
      &:after {
        display: flex;
      }

      /*flex布局*/
      @each $flexType, $flexValue in $flexs {
        &.pl-row-flex-#{$flexType} {
          @each $key, $value in $flexValue {
            #{$key}: #{$value}
          }
        }
      }
    }

    /*响应式*/
    .pl-col {
      display: block;
      float: left;

      @for $i from 1 through $cell-num {
        &.pl-col-span-#{$i} {
          width: (1 / $cell-num * $i * 100) * 1%;
        }
        &.pl-col-offset-#{$i} {
          margin-left: (1 / $cell-num * $i * 100) * 1%;
        }
        &.pl-col-pull-#{$i} {
          position: relative;
          right: (1 / $cell-num * $i * 100) * 1%;
        }
        &.pl-col-push-#{$i} {
          position: relative;
          left: (1 / $cell-num * $i * 100) * 1%;
        }
        &.pl-col-order-#{$i} {
          order: $i;
        }
      }

      @each $size, $px in $screens {
        @include res(inspect($size)) {
          @for $i from 1 through $cell-num {
            &.pl-col-#{$size}-#{$i} {
              width: (1 / $cell-num * $i * 100) * 1%;
            }
          }
        }
      }
    }
  }

  @each $size, $px in $screens {
    @include res(inspect($size)) {
      .hidden-#{$size}-only {
        display: none;
      }
    }
  }
}

.pl-radio-inner {

  width: 1.1em;
  height: 1.1em;

  display: inline-block;
  box-sizing: border-box;
  border-color: currentColor;
  border-width: 1px;
  border-style: solid;
  border-radius: 100px;

  circle {
    fill: currentColor;
    stroke: currentColor;
  }
}

@include theme {
  .pl-rate {
    position: relative;
    display: inline-block;
    cursor: pointer;
    overflow: hidden;
    vertical-align: text-bottom;

    .pl-rate-active {
      white-space: nowrap;
      overflow: hidden;
      position: absolute;
      left: 0;
      top: 0;
      bottom: 0;
    }

    @include statusMixin(rate) {
      color: $value;
    }

    &.pl-rate-disabled {
      color: $disabledText !important;
      cursor: not-allowed;
    }

    &.pl-rate-size-min {
