@include theme {
  .pl-slider {
    @include public-style;
    cursor: pointer;
    display: inline-block;
    position: relative;
    background-color: rgba(black, 0.15);
    border-radius: 100px;

    .pl-slider-progress {
      border-radius: 100px;
      position: absolute;
      top: 0;
      bottom: 0;
      left: 0;
      right: 0;
      display: flex;
      align-items: center;
      justify-content: space-between;

      .pl-slider-dot-wrapper {
        width: 0;
        height: 0;
        overflow: visible;
        display: flex;
        align-items: center;
        justify-content: center;
        user-select: none;

        .pl-slider-dot {
          height: 16px;
          width: 16px;
          background-color: white;
          box-shadow: 0 0 6px #aaa;
          border-radius: 100px;
          display: flex;
          align-items: center;
          justify-content: center;
          flex-shrink: 0;
          transition: all 0.15s cubic-bezier(0, 1.55, 1, 1.52);
          transform-origin: center center;

          .pl-slider-dot-inner {
            width: 6px;
            height: 6px;
            border-radius: 100px;
            position: relative;

            .pl-slider-dot-tooltip {
              transition: all 300ms $transition;
              opacity: 0;
              background-color: rgba(black, 0.8);
              color: white;
              padding: 3px 6px;
              position: absolute;
              left: 50%;
              transform: translateX(-50%);
              bottom: calc(100% + 9px);
              border-radius: 4px;
              font-size: 12px;
              pointer-events: none;
              &:hover {
                opacity: 0.5;
              }
            }
          }

          &:hover {
            .pl-slider-dot-tooltip {
              opacity: 1;
            }
          }
        }
      }
    }

    .pl-slider-step {
      position: absolute;
      top: 0;
      bottom: 0;
      left: 0;
      right: 0;
      display: flex;
      align-items: center;
      justify-content: space-evenly;

      .pl-slider-step-item-wrapper {
        display: inline-flex;
        align-items: center;
        justify-content: center;
        width: 0;
        height: 0;
        overflow: visible;

        .pl-slider-step-item {
          background-color: rgba(black, 0.3);
          border-radius: 100px;
          flex-shrink: 0;
        }
      }
    }

    &.pl-slider-touching {
      .pl-slider-dot {
        transform: scale(1.4);

        .pl-slider-dot-tooltip {
          opacity: 1 !important;
        }
      }
    }

    .pl-slider-dot:hover {
      transform: scale(1.4);
      transform-origin: center center;
      transition: all 0.15s cubic-bezier(0, 1.55, 1, 1.52);
    }

    @include statusMixin(slider) {
      .pl-slider-progress {
        &, & .pl-slider-dot-inner {
          background-color: $value;
        }
      }
    }

    &.pl-slider-vertical {
      .pl-slider-progress, .pl-slider-step {
        display: flex;
        flex-direction: column;
      }
    }

    &.pl-slider-horizontal {
      .pl-slider-progress, .pl-slider-step {
        display: flex;
        flex-direction: row;
      }
    }

    &.pl-slider-align-start {
      .pl-slider-dot-wrapper-start {
        opacity: 0;
        z-index: 0;
      }

      .pl-slider-dot-wrapper-end {
        z-index: 1;
      }
    }

    &.pl-slider-align-end {
      .pl-slider-dot-wrapper-end {
        opacity: 0;
        z-index: 0;
      }

      .pl-slider-dot-wrapper-start {
        z-index: 1;
      }
    }

    &.pl-slider-range {
      .pl-slider-dot-wrapper {
        opacity: 1;
      }
    }

    &.pl-slider-place-end {
      .pl-slider-dot-wrapper-start {
        z-index: 2;
      }
    }
  }
}

@include theme {
    .pl-step-group {

        .pl-step {
            .pl-step-title {
                font-size: 16px;
                color: $ihc;
            }

            .pl-step-content {
                font-size: 14px;
            }

            .pl-step-dot {
                width: 12px;
                height: 12px;
                border-radius: 12px;
                display: inline-block;
            }
        }

        &:not(.pl-step-group-dot-icon ) {
            .pl-step-icon {
                width: 32px;
                height: 32px;
                font-size: 16px;
                line-height: 32px;
                text-align: center;
                border-radius: 32px;
                border: solid 1px $ibc;
                display: inline-block;
                color: $icc;
                margin: 0 8px;

                .pl-icon, .pl-loading {
                    font-size: 1.8em;
                }

                .pl-step-number {
                    .pl-icon {
                        font-size: 1.2em;
                    }
                }
            }
        }

        &.pl-step-group-horizontal {
            display: flex;
            align-items: center;

            .pl-step {
                flex: 1;

                .pl-step-divider {
                    flex: 1;
                    height: 1px;
                    background-color: $ibl;
                }

                .pl-step-head, .pl-step-body {
                    display: flex;
                }

                .pl-step-head {
                    align-items: center;
                    margin-bottom: 8px;

                    .pl-step-title {
                        margin-right: 8px;
                    }

                    .pl-step-dot {
                        margin-right: 8px;
                    }
                }

                .pl-step-body {
                    align-items: flex-start;

                    .pl-step-icon {
                        border: none;
                    }

                    .pl-step-title {
                        margin-bottom: 8px;
                    }
                }


                &.pl-step-last {
                    flex: initial;
                }
            }

            &.pl-step-group-title-align-bottom {
                .pl-step {
                    &:first-child {
                        .pl-step-head {
                            .pl-step-divider:first-child {
                                background-color: transparent !important;
                            }
                        }
                    }

                    &:last-child {
                        flex: 1;

                        .pl-step-head {
                            .pl-step-divider:last-child {
                                background-color: transparent !important;
                            }
                        }
                    }
                }

                .pl-step-body {
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    justify-content: center;
                }
            }
        }

        &.pl-step-group-vertical {
            display: flex;
            flex-direction: column;
            flex-wrap: revert;

            .pl-step {
                display: flex;

                .pl-step-head {
                    display: flex;
                    flex-direction: column;
                    align-items: center;

                    .pl-step-divider {
                        flex: 1;
                        width: 1px;
                        background-color: $ibl;
                    }
                }

                .pl-step-body {
                    flex: 1;
                    display: flex;
                    flex-direction: column;

                    .pl-step-title {
                        line-height: 32px;
                    }

                    .pl-step-content {
                        padding: 8px 0;
                    }
                }

                &:last-child {
                    .pl-step-head .pl-step-divider {
                        display: none;
                    }
                }
            }
        }

        .pl-step {
            &.pl-step-has-icon {
                .pl-step-head {
                    .pl-step-icon {
                        border: none;
                        position: relative;
                        top: 1px;
                    }
                }
            }

            &.pl-step-status-wait {
                .pl-step-title {
                    color: $icc;
                }

                .pl-step-content {
                    color: $icc;
                }

                .pl-step-dot {
                    border: solid 1px $icc;
                    background-color: white;
                }
            }

            &.pl-step-status-finish {
                .pl-step-head {
                    .pl-step-icon {
                        border-color: $colorPrimary;
                        color: $colorPrimary;
                    }

                    .pl-step-dot {
                        background-color: $colorPrimary;
                    }

                    .pl-step-divider.pl-step-divider-next {
                        background-color: $colorPrimary;
                    }
                }

                & + .pl-step {
                    .pl-step-head {
                        .pl-step-divider-prev {
                            background-color: $colorPrimary;
                        }
                    }
                }
            }

            &.pl-step-status-process {

                .pl-step-head {
                    .pl-step-icon {
                        border-color: $colorPrimary;
                        background-color: $colorPrimary;
                        color: white;
                    }

                    .pl-step-title {
                        color: $colorPrimary;
                    }

                    .pl-step-dot {
                        border: solid 4px $colorPrimary;
                        box-sizing: border-box;
                        background-color: white;
                    }
                }

                .pl-step-body {
                    .pl-step-content {
                        color: $colorPrimary;
                    }

                    .pl-step-title {
                        color: $colorPrimary;
                    }
                }

                &.pl-step-has-icon {
                    .pl-step-icon {
                        border-color: transparent;
                        background-color: transparent;
                        color: $colorPrimary;
                    }
                }
            }

            &.pl-step-status-error {

                .pl-step-head {
                    .pl-step-icon {
                        border-color: $colorError;
                        background-color: $colorError;
                        color: white;
                    }

                    .pl-step-title {
                        color: $colorError;
                    }

                    .pl-step-dot {
                        background-color: $colorError;
                    }
                }

                .pl-step-body {
                    .pl-step-content {
                        color: $colorError;
                    }

                    .pl-step-title {
                        color: $colorError;
                    }
                }

                &.pl-step-has-icon {
                    .pl-step-icon {
                        border-color: transparent;
                        background-color: transparent;
                        color: $colorError;
                    }
                }
            }
        }

        &.pl-step-group-mini {

            &:not(.pl-step-group-dot-icon) {
                .pl-step-icon {
                    width: 20px;
                    height: 20px;
                    font-size: 12px;
                    line-height: 20px;
                    text-align: center;
                    border-radius: 20px;
                    border: solid 1px $ibc;
                    display: inline-block;
                    color: $icc;
                    margin: 0 8px;

                    .pl-icon, .pl-loading {
                        font-size: 1.8em;
                    }

                    .pl-step-number {
                        .pl-icon {
                            font-size: 1.2em;
                        }
                    }
                }
            }

            .pl-step-title {
                font-size: 14px;
            }

            .pl-step-content {
                font-size: 12px;
            }

            &.pl-step-group-vertical {
                .pl-step {
                    .pl-step-body {
                        .pl-step-title {
                            line-height: 20px;
                        }
                    }
                }
            }
        }

        &.pl-step-group-dot-icon {
            .pl-step {
                .pl-step-head {
                    .pl-step-icon {
                        background-color: transparent;
                    }
                }
            }

            &.pl-step-group-horizontal:not(.pl-step-group-vertical) {
                .pl-step {
                    .pl-step-content {
                        padding-left: 20px;
                    }
                }
            }

            &.pl-step-group-title-align-bottom {
                .pl-step .pl-step-head .pl-step-dot {
                    margin-right: 0;
                }
            }

            &.pl-step-group-vertical {
                .pl-step-head {
                    margin-right: 8px;
                    position: relative;

                    .pl-step-divider {
                        position: absolute;
                        left: 5px;
                        top: 22px;
                        bottom: -10px;
                    }
                }

                .pl-step-icon {
                    margin-top: 10px;
                    height: 12px;
                    display: inline-block;

                    .pl-step-dot {
                        float: left;
                    }
                }

                &.pl-step-group-mini {
                    .pl-step-icon {
                        margin-top: 5px;
                    }

                    .pl-step-head {
                        .pl-step-divider {
                            position: absolute;
                            left: 5px;
                            top: 16px;
                            bottom: -5px;
                        }
                    }
                }
            }
        }
    }
}

@include theme {
  /*.pl-inner-tab {
    transform-origin: left top;
    transition: all 500ms $transition;
  }*/
}

@include theme {
  $tableHeadColor: rgba(0, 0, 0, .85);
  $tableHeadBackground: #f9f9f9;
  $tableHeadHoverBackground: #e4e7ef;
  $tableHeadBorderColor: #f0f0f0;

  $tableBodyColor: rgba(0, 0, 0, 0.85);

  $tableRowHoverBackground: #f5f7fa;
  $tableRowCurrentBackground: mix($colorPrimary, white, 7.5%);

  $scrollbarSize: 12px;
  /*---------------------------------------scroll-------------------------------------------*/
  .pl-table, .pl-virtual-table {
    position: relative;

    .pl-scroll {
      .pl-scroll-content {
        min-width: 100%;
      }

      .pl-vertical-scrollbar-wrapper, .pl-horizontal-scrollbar-wrapper {
        z-index: 10;
      }
    }

    &:hover {
      .pl-scroll {
        & > .pl-horizontal-scrollbar-wrapper, & > .pl-vertical-scrollbar-wrapper {
          .pl-vertical-scrollbar, .pl-horizontal-scrollbar {
            opacity: 1;
          }
        }
      }
    }
  }
  /*---------------------------------------虚拟表格-------------------------------------------*/
  .pl-virtual-table {
    height: 100%;
    width: 100%;
    overflow: hidden;

    .pl-virtual-table-summary-table-wrapper {
      pointer-events: none;
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      overflow: hidden;
      z-index: 5;

      & > table {
        &:after {
          content: '';
          display: block;
          height: $scrollbarSize;
          width: 1px;
        }
      }

      &:after {
        position: absolute;
        bottom: 0;
        height: $scrollbarSize;
        left: 0;
        content: "";
        background-color: #f5f7fa;
        z-index: 3;
        width: 5000px;
      }

      .pl-virtual-table-summary-table {
        pointer-events: auto;
        position: absolute;
        left: 0;

        td {
          background-color: $tableHeadBackground;
        }
      }
    }
  }
  /*---------------------------------------basic-------------------------------------------*/
  table {
    border-collapse: collapse;
    border-spacing: 0;
    box-sizing: border-box;
    table-layout: fixed;
    /**
     * table一直都是有个最小宽度100%。
     * 当总列宽小于表格宽度时，table没有具体width宽度，fit列不设置库宽度，使其自适应宽度；
     * 当总列宽大于表格宽度时，table设置具体的width宽度，fit值无效，以列具体宽度为准；
     */
    //min-width: 100%;  // 不能设置min-width，当文字过长的时候，会自动撑开列宽，导致表头与表体错位
    width: 100%;

    tr {
      td {
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        box-sizing: border-box;
        position: relative;
        padding: 1px 11px;
      }
    }
  }
  /*---------------------------------------collector-------------------------------------------*/
  .plc-collector {
    display: none;
  }
  /*---------------------------------------no data-------------------------------------------*/
  .pl-table-no-data {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    pointer-events: none;
  }
  /*---------------------------------------head-------------------------------------------*/
  .plt-head {
    td {
      background-color: $tableHeadBackground;
      color: $tableHeadColor;

      &:hover {
        background-color: $tableHeadHoverBackground;
      }

      .plt-head-cell-indicator {
        position: absolute;
        top: 0;
        bottom: 0;
        height: inherit;
        width: 6px;
        right: 0;
        content: '';
        background-color: rgba(black, 0.1);
        cursor: ew-resize;
        opacity: 0;
        transition: opacity 500ms $transition;
        z-index: 1;
      }

      .plt-head-cell-sorter {
        position: absolute;
        right: 4px;
        top: 0;
        bottom: 0;
        display: flex;
        align-items: center;
        justify-content: center;

        .plt-head-cell-sorter-inner {
          display: flex;
          flex-direction: column;
          color: rgba(black, 0.4);

          .pl-icon:first-child {
            position: relative;
            top: 3px;
          }

          .pl-icon:last-child {
            position: relative;
            bottom: 3px;
          }
        }

        &.plt-head-cell-sorter-asc {
          .plt-head-cell-sorter-inner {
            .pl-icon:first-child {
              color: rgba(black, 0.8);
            }
          }
        }

        &.plt-head-cell-sorter-desc {
          .plt-head-cell-sorter-inner {
            .pl-icon:last-child {
              color: rgba(black, 0.8);
            }
          }
        }
      }
    }
  }
  /*---------------------------------------body-------------------------------------------*/
  .plt-body {
    tr {
      td {
        transition: background-color 150ms linear;
        background-color: white;
        color: $tableBodyColor;
      }
    }
  }
  /*---------------------------------------row-------------------------------------------*/
  .plt-row {
    border-bottom: solid 1px $tableHeadBorderColor;

    &:hover {
      td {
        background-color: $tableRowHoverBackground;
      }
    }

    &.plt-row-current {
      &, &:hover {
        td {
          background-color: $tableRowCurrentBackground;
        }
      }
    }
  }
  .plt-head {
    tr {
      border-bottom: solid 1px $tableHeadBorderColor;
      border-top: solid 1px $tableHeadBorderColor;
    }
  }
  .plt-body {
    .pl-virtual-table-summary-table-wrapper {
      & > table {
        border-top: solid 1px $tableHeadBorderColor;
      }
    }
  }
  /*---------------------------------------cell-------------------------------------------*/
  .plt-cell {
    text-align: left;

    &.plt-cell-align-center {
      text-align: center;
    }

    &.plt-cell-align-right {
      text-align: right;
    }

    &.plt-cell-no-padding {
      padding: 0;
    }

    &.plt-cell-editing {
      &:not(.plt-cell-add-edit-padding) {
        padding-left: 0;
      }
    }

    &.plt-cell-link {
      color: $colorPrimary;
      cursor: pointer;

      &:hover {
        text-decoration: underline;
      }
    }
  }
  /*---------------------------------------border-------------------------------------------*/
  .pl-table {

    &.pl-table-border {
      tr {
        td {
          border-right: solid 1px $tableHeadBorderColor;
          border-left: solid 1px $tableHeadBorderColor;
        }
      }

      &:after {
        position: absolute;
        right: 0;
        bottom: 0;
        top: 0;
        left: 0;
        background-color: transparent;
        border: solid 0.5px $tableHeadBorderColor;
        content: '';
        z-index: 5;
        pointer-events: none;
      }
    }

    &.pl-table-stripe {
      .plt-body {
        .pl-virtual-list-content {
          table {
            tr:nth-child(odd):not(:hover):not(.plt-row-current) {
              td {
                background-color: #f2fff9;
              }
            }
          }
        }
      }
    }
  }
  /*---------------------------------------size-------------------------------------------*/
  .pl-table {
    &.pl-table-size-large {
      .plt-cell {
        font-size: 16px;
      }
    }

    &.pl-table-size-normal {
      .plt-cell {
        font-size: 14px;
      }
    }

    &.pl-table-size-mini {
      .plt-cell {
        font-size: 12px;
      }
    }
  }
  /*---------------------------------------shadow-------------------------------------------*/
  .pl-table {
    &, &.pl-table-border {

      .plt-cell-last-fixed-left, .plt-cell-first-fixed-right {
        //background-color: black;
        //color: white;
        &:before {
          position: absolute;
          top: 0;
          bottom: 0;
          width: 3px;
          pointer-events: none;
          content: '' !important;
          z-index: 4;
          background-color: transparent;

          left: initial;
          right: initial;
          transform: none;
        }

        &.plt-cell-last-fixed-left {
          &:before {
            right: 0;
            box-shadow: inset 10px 0 10px -10px mix(black, $boxshadowColor, 10%);
          }
        }

        &.plt-cell-first-fixed-right {
          &:before {
            left: 0;
            box-shadow: inset -10px 0 10px -10px mix(black, $boxshadowColor, 10%);
          }
        }
      }
    }

    &.plt-table-hide-fixed-left .plt-cell-last-fixed-left, &.plt-table-hide-fixed-right .plt-cell-first-fixed-right {
      &:before {
        display: none;
      }
    }
  }
  /*---------------------------------------standard-------------------------------------------*/
  /*plc-index*/
  .plc-index-summary-text {
    font-size: 12px;
  }
  /*!*icon*!
  .plt-cell {
    & > .pl-icon {
      vertical-align: -0.3em;

      & + * {
        margin-left: 4px;
      }
    }
  }*/
  /*input*/
  .plt-cell {
    & > * {
      vertical-align: middle;

      &.pl-icon {
        vertical-align: middle;
      }
    }

    & > .pl-input {
      width: 100%;

      & > .pl-input-inner {
        width: inherit !important;

        & > .pl-input-custom-inner {
          & > input {
            width: 100% !important;
          }
        }
      }
    }
  }
  /*plc-expand*/
  .plt-row {
    &.plt-expand-row {
      td {
        background-color: white;
        z-index: 5;
        white-space: normal;
        padding: 0;
        overflow: unset;
      }

      .plt-expand-body {
        padding: 16px;
        box-sizing: border-box;
        position: sticky;
        left: 0;

        & > .pl-form {
          padding: 0;
        }
      }
    }

    .plc-expand-icon {
      .pl-icon {
        @include transition;
        transition-property: transform;
      }

      &.plc-expand-icon-active {
        .pl-icon {
          transform: rotate(-180deg);
        }
      }
    }
  }
  /*plc-draggier*/
  .pl-button.plc-draggier-handler {
    cursor: move;
  }
  /*col draggier*/
  .plt-col-draggier-indicator {
    &[droppable="true"] {
      background-color: $colorPrimary;
    }

    &[droppable="false"] {
      background-color: $colorError;
    }
  }
  /*plc-tree*/
  .plc-tree-node {
    white-space: nowrap;

    & > * {
      display: inline-block;
      height: 100%;
    }

    .plc-tree-node-expander, .plc-tree-node-check {
      width: 30px;
      text-align: center;
    }
  }
  .plc-tree-node-content {
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    padding-left: 8px;
    box-sizing: border-box;
  }
}

.pl-table-pro-pagination {
  padding: 16px 24px;

  .pl-pagination {
    justify-content: flex-end;
  }
}

.pl-table-pro-head {
  font-size: 14px;
