        .pl-tabs-header-item {
          border-bottom: solid 1px $ibc;

          &:first-child {
            border-bottom-left-radius: $shapeFillet;
          }

          &:last-child {
            border-bottom-right-radius: $shapeFillet;
          }

          &.pl-tabs-header-item-active {
            position: relative;

            &:before {
              position: absolute;
              background-color: white;
              left: 0;
              right: 0;
              bottom: 100%;
              height: 1px;
              content: '';
