      .pl-tabs-header-horizontal-text-indicator-container {
        background-color: $disabled;
        position: absolute;
        left: 0;
        right: 0;
        height: 2px;

        .pl-tabs-header-horizontal-text-indicator {
          position: absolute;
          width: 0;
          left: 0;
          height: 100%;
          background-color: $colorPrimary;
          display: block;
          transition: $transition 150ms all;
        }
      }
