$transition: cubic-bezier(0.23, 1, 0.32, 1);

/*---------------------------------------scale-------------------------------------------*/

.pl-transition-scale-enter-active, .pl-transition-scale-leave-active {
  transform: scale(1);
  transition: opacity 300ms linear, transform 300ms $transition;
}

.pl-transition-scale-enter-from, .pl-transition-scale-leave-to {
  transform: scale(0.75);
  opacity: 0;
}

/*---------------------------------------scale-y-------------------------------------------*/

.pl-transition-scale-y-enter-active, .pl-transition-scale-y-leave-active {
  transform-origin: top;
  transition: all 300ms $transition;
}

.pl-transition-scale-y-enter-from, .pl-transition-scale-y-leave-to {
  transform: scale(0.75);
  opacity: 0;
}

/*---------------------------------------fade-------------------------------------------*/

.pl-transition-fade-enter-active, .pl-transition-fade-leave-active {
  transition: opacity 300ms linear;
}

.pl-transition-fade-enter-from, .pl-transition-fade-leave-to {
  opacity: 0;
}


/*---------------------------------------slider-------------------------------------------*/

[direction=horizontal] {
  &.pl-transition-slide-next-enter-active, &.pl-transition-slide-next-leave-active {
    transform: translate3d(0, 0, 0);
  }

  &.pl-transition-slide-next-enter-from {
    transform: translate3d(100%, 0, 0);
  }

  &.pl-transition-slide-next-leave-to {
    transform: translate3d(-100%, 0, 0);
  }

  &.pl-transition-slide-prev-enter-active, &.pl-transition-slide-prev-leave-active {
    transform: translate3d(0, 0, 0);
  }

  &.pl-transition-slide-prev-enter-from {
    transform: translate3d(-100%, 0, 0);
  }

  &.pl-transition-slide-prev-leave-to {
    transform: translate3d(100%, 0, 0);
  }
}

[direction=vertical] {
  &.pl-transition-slide-next-enter-active, &.pl-transition-slide-next-leave-active {
    transform: translate3d(0, 0, 0);
  }

  &.pl-transition-slide-next-enter-from {
    transform: translate3d(0, 100%, 0);
  }

  &.pl-transition-slide-next-leave-to {
    transform: translate3d(0, -100%, 0);
  }

  &.pl-transition-slide-prev-enter-active, &.pl-transition-slide-prev-leave-active {
    transform: translate3d(0, 0, 0);
  }

  &.pl-transition-slide-prev-enter-from {
    transform: translate3d(0, -100%, 0);
  }

  &.pl-transition-slide-prev-leave-to {
    transform: translate3d(0, 100%, 0);
  }
}

.pl-collapse-transition {
  transition: 0.3s height ease-in-out, 0.3s padding-top ease-in-out, 0.3s padding-bottom ease-in-out;
}


